}

import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { DollarSign, Loader2, ExternalLink } from 'lucide-react';
import { format } from 'date-fns';
import { createPageUrl } from '@/utils';
import { useNavigate } from 'react-router-dom';

export default function PendingPayoutsDetails() {
  const navigate = useNavigate();
  const [earnings, setEarnings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalUSD, setTotalUSD] = useState(0);

  useEffect(() => {
    loadPendingEarnings();
  }, []);

  const loadPendingEarnings = async () => {
    setLoading(true);
    try {
      const pendingEarnings = await base44.entities.CleanerEarning.filter({ status: 'pending' });

      const sorted = pendingEarnings.sort((a, b) => 
        new Date(b.created_date) - new Date(a.created_date)
      );

      setEarnings(sorted);

      const total = sorted.reduce((sum, e) => sum + (e.usd_due || 0), 0);
      setTotalUSD(total);
    } catch (error) {
      console.error('Error loading pending earnings:', error);
    }
    setLoading(false);
  };

  const getEarningTypeColor = (type) => {
    const colors = {
      job_completed: 'bg-green-100 text-green-800',
      client_no_show_comp: 'bg-blue-100 text-blue-800',
      cancellation_comp: 'bg-purple-100 text-purple-800',
      bonus: 'bg-amber-100 text-amber-800',
      adjustment: 'bg-gray-100 text-gray-800'
    };
    return colors[type] || 'bg-gray-100 text-gray-800';
  };

  const viewBooking = (bookingId) => {
    navigate(createPageUrl('AdminBookingsConsoleV2'));
  };

  if (loading) {
    return (
      <Card className="border-0 shadow-xl rounded-2xl">
        <CardHeader className="bg-gradient-to-r from-amber-50 to-orange-50 rounded-t-2xl">
          <CardTitle className="flex items-center gap-2 font-fredoka text-graphite">
            <DollarSign className="w-6 h-6 text-amber-600" />
            Pending Payouts Details
          </CardTitle>
        </CardHeader>
        <CardContent className="p-8 flex justify-center">
          <Loader2 className="w-8 h-8 animate-spin text-puretask-blue" />
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="border-0 shadow-xl rounded-2xl">
      <CardHeader className="bg-gradient-to-r from-amber-50 to-orange-50 rounded-t-2xl">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2 font-fredoka text-graphite">
            <DollarSign className="w-6 h-6 text-amber-600" />
            Pending Payouts Details
          </CardTitle>
          <div className="text-right">
            <p className="text-sm text-gray-600 font-verdana">Total Pending</p>
            <p className="text-3xl font-fredoka font-bold text-amber-600">${totalUSD.toFixed(2)}</p>
          </div>
        </div>
      </CardHeader>
      <CardContent className="p-6">
        <div className="mb-4 flex items-center justify-between">
          <p className="text-sm text-gray-600 font-verdana">
            Showing <span className="font-medium text-graphite">{earnings.length}</span> pending earning{earnings.length !== 1 ? 's' : ''}
          </p>
          <Button
            variant="outline"
            size="sm"
            onClick={loadPendingEarnings}
            className="rounded-full font-fredoka"
          >
            Refresh
          </Button>
        </div>

        {earnings.length === 0 ? (
          <div className="text-center py-12 bg-gray-50 rounded-2xl">
            <DollarSign className="w-16 h-16 text-gray-300 mx-auto mb-3" />
            <p className="text-gray-500 font-verdana text-lg">No pending payouts</p>
            <p className="text-sm text-gray-400 font-verdana mt-1">All earnings have been processed</p>
          </div>
        ) : (
          <div className="overflow-x-auto rounded-2xl border border-gray-200">
            <Table>
              <TableHeader>
                <TableRow className="bg-gray-50">
                  <TableHead className="font-fredoka">Cleaner</TableHead>
                  <TableHead className="font-fredoka">Type</TableHead>
                  <TableHead className="font-fredoka">Booking ID</TableHead>
                  <TableHead className="font-fredoka text-right">Credits</TableHead>
                  <TableHead className="font-fredoka text-right">Payout %</TableHead>
                  <TableHead className="font-fredoka text-right">USD Due</TableHead>
                  <TableHead className="font-fredoka">Date</TableHead>
                  <TableHead className="font-fredoka">Action</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {earnings.map((earning) => (
                  <TableRow key={earning.id} className="hover:bg-gray-50">
                    <TableCell className="font-verdana text-sm font-medium">
                      {earning.cleaner_email}
                    </TableCell>
                    <TableCell>
                      <Badge className={getEarningTypeColor(earning.earning_type)}>
                        {earning.earning_type.replace(/_/g, ' ')}
                      </Badge>
                    </TableCell>
                    <TableCell className="font-verdana text-sm text-gray-600">
                      {earning.booking_id ? (
                        <span className="font-mono text-xs">{earning.booking_id.slice(0, 8)}...</span>
                      ) : (
                        <span className="text-gray-400">-</span>
                      )}
                    </TableCell>
                    <TableCell className="text-right font-fredoka font-semibold text-puretask-blue">
                      {earning.credits_earned?.toLocaleString() || 0}
                    </TableCell>
                    <TableCell className="text-right font-verdana text-sm">
                      {((earning.payout_percentage || 0) * 100).toFixed(0)}%
                    </TableCell>
                    <TableCell className="text-right font-fredoka font-bold text-green-600">
                      ${(earning.usd_due || 0).toFixed(2)}
                    </TableCell>
                    <TableCell className="font-verdana text-sm text-gray-600">
                      {format(new Date(earning.created_date), 'MMM dd, yyyy')}
                    </TableCell>
                    <TableCell>
                      {earning.booking_id && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => viewBooking(earning.booking_id)}
                          className="h-8 px-2"
                        >
                          <ExternalLink className="w-4 h-4" />
                        </Button>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Sparkles, Bot, Calendar, TrendingUp, Clock, CheckCircle, Loader2 } from 'lucide-react';
import AgentApprovalCard from './AgentApprovalCard';
import AgentChatInterface from './AgentChatInterface';
import { toast } from 'sonner';

export default function AgentDashboard({ cleanerEmail }) {
  const [pendingActions, setPendingActions] = useState([]);
  const [agentActive, setAgentActive] = useState(true);
  const [loading, setLoading] = useState(true);
  const [chatOpen, setChatOpen] = useState(false);
  const [dailySummary, setDailySummary] = useState(null);

  useEffect(() => {
    loadPendingActions();
    loadDailySummary();
  }, [cleanerEmail]);

  const loadPendingActions = async () => {
    try {
      const response = await base44.functions.invoke('agentAssistant', {
        action: 'getPendingActions',
        cleaner_email: cleanerEmail
      });

      if (response.data.success) {
        setPendingActions(response.data.actions);
      }
    } catch (error) {
      console.error('Error loading pending actions:', error);
    }
    setLoading(false);
  };

  const loadDailySummary = async () => {
    try {
      const response = await base44.functions.invoke('agentAssistant', {
        action: 'getLatestSummary',
        cleaner_email: cleanerEmail
      });

      if (response.data.success && response.data.summary) {
        setDailySummary(response.data.summary);
      }
    } catch (error) {
      console.error('Error loading summary:', error);
    }
  };

  const handleApproval = async (actionId, approved) => {
    try {
      const response = await base44.functions.invoke('agentAssistant', {
        action: 'respondToAction',
        action_id: actionId,
        approved: approved
      });

      if (response.data.success) {
        toast.success(approved ? 'Action approved! Agent is processing...' : 'Action declined');
        loadPendingActions();
      }
    } catch (error) {
      console.error('Error responding to action:', error);
      toast.error('Failed to respond');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-8">
        <Loader2 className="w-8 h-8 animate-spin text-puretask-blue" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Agent Status */}
      <Card className="border-2 border-blue-200 bg-gradient-to-br from-blue-50 to-purple-50">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 rounded-full bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center shadow-lg">
                <Bot className="w-6 h-6 text-white" />
              </div>
              <div>
                <CardTitle className="text-xl font-fredoka">Your AI Assistant</CardTitle>
                <p className="text-sm text-gray-600 font-verdana">Managing your schedule 24/7</p>
              </div>
            </div>
            <Badge className={agentActive ? 'bg-green-600' : 'bg-gray-400'}>
              {agentActive ? 'Active' : 'Paused'}
            </Badge>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid md:grid-cols-3 gap-4">
            <div className="p-3 bg-white rounded-lg">
              <div className="flex items-center gap-2 mb-1">
                <Calendar className="w-4 h-4 text-blue-600" />
                <span className="text-xs font-verdana text-gray-600">Pending Approvals</span>
              </div>
              <p className="text-2xl font-fredoka font-bold text-blue-600">{pendingActions.length}</p>
            </div>
            <div className="p-3 bg-white rounded-lg">
              <div className="flex items-center gap-2 mb-1">
                <CheckCircle className="w-4 h-4 text-green-600" />
                <span className="text-xs font-verdana text-gray-600">Auto-Actions Today</span>
              </div>
              <p className="text-2xl font-fredoka font-bold text-green-600">{dailySummary?.auto_actions_count || 0}</p>
            </div>
            <div className="p-3 bg-white rounded-lg">
              <div className="flex items-center gap-2 mb-1">
                <TrendingUp className="w-4 h-4 text-purple-600" />
                <span className="text-xs font-verdana text-gray-600">Schedule Optimization</span>
              </div>
              <p className="text-2xl font-fredoka font-bold text-purple-600">98%</p>
            </div>
          </div>

          <Button 
            onClick={() => setChatOpen(true)}
            className="w-full mt-4 brand-gradient text-white"
          >
            <Sparkles className="w-4 h-4 mr-2" />
            Chat with AI Assistant
          </Button>
        </CardContent>
      </Card>

      {/* Daily Summary */}
      {dailySummary && (
        <Card>
          <CardHeader>
            <CardTitle className="font-fredoka flex items-center gap-2">
              <Clock className="w-5 h-5 text-puretask-blue" />
              Latest Daily Summary
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <div className="text-sm font-verdana">
              <p className="text-gray-600 mb-2">
                Generated: {new Date(dailySummary.created_date).toLocaleString()}
              </p>

              <div className="prose prose-sm max-w-none">
                <div dangerouslySetInnerHTML={{ __html: dailySummary.summary_html }} />
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Pending Actions */}
      {pendingActions.length > 0 && (
        <div className="space-y-4">
          <h3 className="text-lg font-fredoka font-semibold">Actions Awaiting Your Approval</h3>
          {pendingActions.map(action => (
            <AgentApprovalCard 
              key={action.id} 
              action={action}
              onApprove={() => handleApproval(action.id, true)}
              onDecline={() => handleApproval(action.id, false)}
            />
          ))}
        </div>
      )}

      {pendingActions.length === 0 && (
        <Card>
          <CardContent className="py-8 text-center">
            <CheckCircle className="w-12 h-12 text-green-600 mx-auto mb-3" />
            <p className="font-fredoka text-lg">All caught up!</p>
            <p className="text-sm text-gray-600 font-verdana">No pending actions right now</p>
          </CardContent>
        </Card>
      )}

      {/* Chat Interface */}
      {chatOpen && (
        <AgentChatInterface 
          cleanerEmail={cleanerEmail}
          onClose={() => setChatOpen(false)}
        />
      )}
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Send, Copy, Sparkles, 
  CheckCircle, Loader2, RefreshCw, Lightbulb
} from 'lucide-react';
import { format } from 'date-fns';

export default function AIResponseSuggestions({ threadId, clientMessage }) {
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [sendingIndex, setSendingIndex] = useState(null);
  const [copied, setCopied] = useState(false);

  useEffect(() => {
    if (clientMessage) {
      generateSuggestions(clientMessage);
    }
  }, [clientMessage]);

  const generateSuggestions = async (message) => {
    setLoading(true);
    try {
      const currentUser = await base44.auth.me();

      // Get cleaner profile for context
      const profiles = await base44.entities.CleanerProfile.filter({
        user_email: currentUser.email
      });

      const profile = profiles[0];

      // Get booking context if available
      const threads = await base44.entities.ConversationThread.filter({ id: threadId });
      const thread = threads[0];

      let bookingContext = '';
      if (thread?.booking_id) {
        const bookings = await base44.entities.Booking.filter({ id: thread.booking_id });
        if (bookings.length > 0) {
          const booking = bookings[0];
          bookingContext = `Booking: ${format(new Date(booking.date), 'MMM d')} at ${booking.start_time}, ${booking.hours}h, ${booking.address}`;
        }
      }

      // Use AI to generate contextual responses
      const prompt = `You are a professional cleaning service provider. Generate 3 helpful response options for this client inquiry:

Client Message: "${message}"

Context:
- Cleaner: ${profile.full_name}
- Rating: ${profile.average_rating}/5
- ${bookingContext}

Generate 3 response options:
1. Professional and detailed
2. Friendly and warm
3. Quick and concise

Make them natural, helpful, and appropriate for the context.`;

      const aiResponse = await base44.integrations.Core.InvokeLLM({
        prompt,
        response_json_schema: {
          type: 'object',
          properties: {
            responses: {
              type: 'array',
              items: {
                type: 'object',
                properties: {
                  text: { type: 'string' },
                  style: { type: 'string' },
                  tone: { type: 'string' }
                }
              }
            }
          }
        }
      });

      const generated = aiResponse.responses || [];
      setSuggestions(generated.map((r, i) => ({
        id: i,
        text: r.text,
        style: r.style || 'Professional',
        tone: r.tone || 'Helpful'
      })));

    } catch (error) {
      console.error('Error generating suggestions:', error);

      // Fallback suggestions based on common patterns
      const fallbackSuggestions = generateFallbackSuggestions(message);
      setSuggestions(fallbackSuggestions);
    }
    setLoading(false);
  };

  const generateFallbackSuggestions = (message) => {
    const lowerMessage = message.toLowerCase();

    if (lowerMessage.includes('available') || lowerMessage.includes('schedule')) {
      return [
        {
          id: 1,
          text: "Yes, I'm available! What date and time works best for you? I typically have openings throughout the week.",
          style: 'Professional',
          tone: 'Eager'
        },
        {
          id: 2,
          text: "I'd be happy to help! Let me know which day you prefer and I'll check my schedule for available time slots.",
          style: 'Warm',
          tone: 'Friendly'
        },
        {
          id: 3,
          text: "Available! What day/time? üòä",
          style: 'Casual',
          tone: 'Quick'
        }
      ];
    }

    if (lowerMessage.includes('price') || lowerMessage.includes('cost') || lowerMessage.includes('rate')) {
      return [
        {
          id: 1,
          text: "My rates depend on the size of your space and type of cleaning. For a detailed quote, could you share your home size (sq ft or bedrooms/bathrooms) and whether you need a basic, deep, or move-out clean?",
          style: 'Professional',
          tone: 'Informative'
        },
        {
          id: 2,
          text: "I'd love to give you an accurate quote! Can you tell me a bit about your space - how many bedrooms and bathrooms, and what type of cleaning you're looking for?",
          style: 'Warm',
          tone: 'Helpful'
        },
        {
          id: 3,
          text: "Rates vary by home size and cleaning type. What are your space details?",
          style: 'Concise',
          tone: 'Direct'
        }
      ];
    }

    if (lowerMessage.includes('supplies') || lowerMessage.includes('products') || lowerMessage.includes('bring')) {
      return [
        {
          id: 1,
          text: "I bring all my own professional cleaning supplies and equipment! If you have specific product preferences or allergies, just let me know and I'm happy to accommodate.",
          style: 'Professional',
          tone: 'Reassuring'
        },
        {
          id: 2,
          text: "Don't worry - I come fully equipped with all supplies! I use high-quality, effective products. If you prefer eco-friendly or have any sensitivities, I can definitely work with that.",
          style: 'Warm',
          tone: 'Accommodating'
        },
        {
          id: 3,
          text: "Yes, I bring everything! Can customize products if needed.",
          style: 'Quick',
          tone: 'Efficient'
        }
      ];
    }

    // Generic helpful responses
    return [
      {
        id: 1,
        text: "Thank you for your message! I'd be happy to help with that. Could you provide a few more details so I can give you the best answer?",
        style: 'Professional',
        tone: 'Polite'
      },
      {
        id: 2,
        text: "Hi! Thanks for reaching out. I'd love to assist you with this. What additional information would be helpful?",
        style: 'Friendly',
        tone: 'Warm'
      },
      {
        id: 3,
        text: "Happy to help! Can you share more details?",
        style: 'Casual',
        tone: 'Brief'
      }
    ];
  };

  const handleSendSuggestion = async (suggestion) => {
    setSendingIndex(suggestion.id);
    try {
      const currentUser = await base44.auth.me();

      // Send the suggested response
      await base44.entities.Message.create({
        thread_id: threadId,
        sender_email: currentUser.email,
        content: suggestion.text,
        type: 'user_message',
        timestamp: new Date().toISOString(),
        metadata: {
          ai_suggested: true,
          suggestion_style: suggestion.style
        }
      });

      // Update thread
      const threads = await base44.entities.ConversationThread.filter({ id: threadId });
      if (threads.length > 0) {
        await base44.entities.ConversationThread.update(threadId, {
          last_message_at: new Date().toISOString(),
          last_message_content: suggestion.text.substring(0, 100),
          unread_count_client: (threads[0].unread_count_client || 0) + 1
        });
      }

      // Clear suggestions after sending
      setSuggestions([]);

    } catch (error) {
      console.error('Error sending suggestion:', error);
    }
    setSendingIndex(null);
  };

  const handleCopySuggestion = (text) => {
    navigator.clipboard.writeText(text);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  if (loading) {
    return (
      <Card className="border-2 border-dashed border-blue-200">
        <CardContent className="p-6 text-center">
          <Loader2 className="w-8 h-8 animate-spin text-puretask-blue mx-auto mb-2" />
          <p className="text-sm text-slate-600 font-verdana">AI is generating response suggestions...</p>
        </CardContent>
      </Card>
    );
  }

  if (suggestions.length === 0 || !clientMessage) {
    return null;
  }

  return (
    <Card className="border-2 border-blue-200 bg-gradient-to-br from-blue-50 to-purple-50">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-fredoka flex items-center gap-2">
            <Sparkles className="w-5 h-5 text-puretask-blue" />
            AI-Generated Response Suggestions
          </CardTitle>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => generateSuggestions(clientMessage)}
          >
            <RefreshCw className="w-4 h-4" />
          </Button>
        </div>
        <p className="text-xs text-slate-600 font-verdana">
          Select a response to send or customize it further
        </p>
      </CardHeader>
      <CardContent className="space-y-3">
        {suggestions.map((suggestion, index) => (
          <div
            key={suggestion.id}
            className="bg-white border-2 border-slate-200 rounded-lg p-4 hover:border-puretask-blue transition-colors"
          >
            <div className="flex items-start justify-between mb-2">
              <div className="flex gap-2">
                <Badge variant="outline" className="text-xs">
                  {suggestion.style}
                </Badge>
                <Badge variant="outline" className="text-xs text-purple-700 border-purple-300">
                  {suggestion.tone}
                </Badge>
              </div>
            </div>

            <p className="text-sm text-slate-700 font-verdana mb-3 leading-relaxed">
              {suggestion.text}
            </p>

            <div className="flex gap-2">
              <Button
                size="sm"
                onClick={() => handleSendSuggestion(suggestion)}
                disabled={sendingIndex === suggestion.id}
                className="flex-1 brand-gradient text-white"
              >
                {sendingIndex === suggestion.id ? (
                  <>
                    <Loader2 className="w-3 h-3 mr-1 animate-spin" />
                    Sending...
                  </>
                ) : (
                  <>
                    <Send className="w-3 h-3 mr-1" />
                    Send This
                  </>
                )}
              </Button>
              <Button
                size="sm"
                variant="outline"
                onClick={() => handleCopySuggestion(suggestion.text)}
              >
                {copied ? (
                  <CheckCircle className="w-3 h-3 text-green-600" />
                ) : (
                  <Copy className="w-3 h-3" />
                )}
              </Button>
            </div>
          </div>
        ))}

        <Alert className="bg-blue-50 border-blue-200">
          <Lightbulb className="w-4 h-4 text-blue-600" />
          <AlertDescription className="text-xs font-verdana">
            These AI-generated responses are suggestions. Feel free to customize them to match your personal style!
          </AlertDescription>
        </Alert>
      </CardContent>
    </Card>
  );
}

import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Calendar, Clock, MapPin, User, TrendingUp, Sparkles, Check, X, Loader2 } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function AISchedulingSuggestions({ cleanerEmail }) {
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(null);

  useEffect(() => {
    loadSuggestions();
  }, [cleanerEmail]);

  const loadSuggestions = async () => {
    try {
      setLoading(true);

      // Get cleaner profile
      const profiles = await base44.entities.CleanerProfile.filter({ user_email: cleanerEmail });
      if (profiles.length === 0) return;

      const profile = profiles[0];

      // Get client preferences that match cleaner's specialties
      const allClients = await base44.entities.ClientProfile.list();

      // Get cleaner's bookings to analyze gaps
      const bookings = await base44.entities.Booking.filter({ cleaner_email: cleanerEmail });

      // Generate AI suggestions using LLM
      const prompt = `You are an AI scheduling assistant for a cleaning professional.

Cleaner Profile:
- Specialties: ${profile.specialty_tags?.join(', ') || 'General cleaning'}
- Tier: ${profile.tier}
- Service Locations: ${profile.service_locations?.join(', ') || 'Not specified'}
- Instant Book: ${profile.instant_book_enabled ? 'Yes' : 'No'}
- Current Bookings: ${bookings.length}

Analyze the cleaner's schedule for the next 14 days and suggest optimal booking slots that:
1. Fill gaps in their schedule
2. Match clients with similar preferences
3. Maximize earnings by grouping nearby locations
4. Consider the cleaner's typical acceptance patterns

Return a JSON array of 3-5 suggestions with this structure:
[
  {
    "client_name": "Potential Client Name",
    "date": "2025-01-15",
    "time": "10:00 AM",
    "duration": 3,
    "location": "Area name",
    "reason": "Why this is a good match",
    "estimated_earnings": 120,
    "match_score": 85,
    "client_preferences": ["pet-friendly", "eco-products"]
  }
]`;

      const response = await base44.integrations.Core.InvokeLLM({
        prompt: prompt,
        response_json_schema: {
          type: "object",
          properties: {
            suggestions: {
              type: "array",
              items: {
                type: "object",
                properties: {
                  client_name: { type: "string" },
                  date: { type: "string" },
                  time: { type: "string" },
                  duration: { type: "number" },
                  location: { type: "string" },
                  reason: { type: "string" },
                  estimated_earnings: { type: "number" },
                  match_score: { type: "number" },
                  client_preferences: { type: "array", items: { type: "string" } }
                }
              }
            }
          }
        }
      });

      setSuggestions(response.suggestions || []);
    } catch (error) {
      console.error('Error loading suggestions:', error);
    }
    setLoading(false);
  };

  const handleAccept = async (suggestion) => {
    setProcessing(suggestion);
    // In a real implementation, this would reach out to the client
    await new Promise(resolve => setTimeout(resolve, 1500));
    setSuggestions(prev => prev.filter(s => s !== suggestion));
    setProcessing(null);
  };

  const handleDecline = (suggestion) => {
    setSuggestions(prev => prev.filter(s => s !== suggestion));
  };

  if (loading) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <Loader2 className="w-8 h-8 animate-spin text-puretask-blue mx-auto mb-2" />
          <p className="text-sm text-gray-600">Analyzing optimal time slots...</p>
        </CardContent>
      </Card>
    );
  }

  if (suggestions.length === 0) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <Sparkles className="w-12 h-12 text-gray-300 mx-auto mb-3" />
          <p className="text-gray-600 font-verdana">No scheduling suggestions at the moment.</p>
          <Button onClick={loadSuggestions} variant="outline" className="mt-3">
            Refresh Suggestions
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <AnimatePresence>
        {suggestions.map((suggestion, idx) => (
          <motion.div
            key={idx}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, x: -100 }}
            transition={{ delay: idx * 0.1 }}
          >
            <Card className="border-2 border-blue-200 bg-gradient-to-r from-blue-50 to-cyan-50">
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex items-center gap-2">
                    <Sparkles className="w-5 h-5 text-puretask-blue" />
                    <CardTitle className="text-lg font-fredoka">AI Suggested Booking</CardTitle>
                  </div>
                  <Badge className="bg-puretask-blue text-white font-fredoka">
                    {suggestion.match_score}% Match
                  </Badge>
                </div>
              </CardHeader>
              <CardContent>
                <div className="grid md:grid-cols-2 gap-4 mb-4">
                  <div className="space-y-2">
                    <div className="flex items-center gap-2 text-sm">
                      <User className="w-4 h-4 text-gray-600" />
                      <span className="font-verdana">{suggestion.client_name}</span>
                    </div>
                    <div className="flex items-center gap-2 text-sm">
                      <Calendar className="w-4 h-4 text-gray-600" />
                      <span className="font-verdana">{suggestion.date} at {suggestion.time}</span>
                    </div>
                    <div className="flex items-center gap-2 text-sm">
                      <Clock className="w-4 h-4 text-gray-600" />
                      <span className="font-verdana">{suggestion.duration} hours</span>
                    </div>
                    <div className="flex items-center gap-2 text-sm">
                      <MapPin className="w-4 h-4 text-gray-600" />
                      <span className="font-verdana">{suggestion.location}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center gap-2 text-sm">
                      <TrendingUp className="w-4 h-4 text-fresh-mint" />
                      <span className="font-verdana font-bold text-fresh-mint">
                        ${suggestion.estimated_earnings} estimated
                      </span>
                    </div>
                    <div className="flex flex-wrap gap-1">
                      {suggestion.client_preferences?.map((pref, i) => (
                        <Badge key={i} variant="outline" className="text-xs">
                          {pref}
                        </Badge>
                      ))}
                    </div>
                  </div>
                </div>

                <div className="bg-white rounded-lg p-3 mb-4">
                  <p className="text-sm text-gray-700 font-verdana">
                    <span className="font-semibold">Why this is a good match:</span> {suggestion.reason}
                  </p>
                </div>

                <div className="flex gap-2">
                  <Button
                    onClick={() => handleAccept(suggestion)}
                    disabled={processing === suggestion}
                    className="flex-1 brand-gradient text-white font-fredoka"
                  >
                    {processing === suggestion ? (
                      <>
                        <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                        Reaching Out...
                      </>
                    ) : (
                      <>
                        <Check className="w-4 h-4 mr-2" />
                        Express Interest
                      </>
                    )}
                  </Button>
                  <Button
                    onClick={() => handleDecline(suggestion)}
                    disabled={processing === suggestion}
                    variant="outline"
                    className="flex-1 font-fredoka"
                  >
                    <X className="w-4 h-4 mr-2" />
                    Not Interested
                  </Button>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        ))}
      </AnimatePresence>

      <Button onClick={loadSuggestions} variant="outline" className="w-full font-fredoka">
        <Sparkles className="w-4 h-4 mr-2" />
        Refresh Suggestions
      </Button>
    </div>
  );
}

import React from 'react';
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from '@/components/ui/tooltip';
import AIBadge from './AIBadge';

export default function AITooltip({ variant, reason, children }) {
  if (!reason) {
    return <AIBadge variant={variant} />;
  }

  return (
    <TooltipProvider>
      <Tooltip>
        <TooltipTrigger asChild>
          <span className="cursor-help">
            <AIBadge variant={variant} />
          </span>
        </TooltipTrigger>
        <TooltipContent className="max-w-[250px]">
          <p className="text-xs font-verdana">{reason}</p>
        </TooltipContent>
      </Tooltip>
    </TooltipProvider>
  );
}

// CleanerAIChatAssistant - AI Chat Interface for Cleaners
// Context-aware chat assistant integrated into job workflow

import React, { useState, useEffect, useRef } from 'react';
import { aiCleanerChatService, ChatMessage, CleanerContext } from '@/services/aiCleanerChatService';
import type { JobRecord } from '@/types/cleanerJobTypes';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Bot, Send, Loader2, Sparkles, TrendingUp, HelpCircle, Zap, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { toast } from 'sonner';

interface CleanerAIChatAssistantProps {
  currentJob?: JobRecord;
  cleanerId: string;
  cleanerEmail: string;
  stats?: {
    totalJobs: number;
    reliabilityScore: number;
    avgRating: number;
    totalEarnings: number;
  };
  minimized?: boolean;
  onMinimize?: () => void;
}

export default function CleanerAIChatAssistant({
  currentJob,
  cleanerId,
  cleanerEmail,
  stats,
  minimized = false,
  onMinimize
}: CleanerAIChatAssistantProps) {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [showQuickActions, setShowQuickActions] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  
  const context: CleanerContext = {
    cleanerId,
    cleanerEmail,
    currentJob,
    stats
  };

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Welcome message on mount
  useEffect(() => {
    if (messages.length === 0) {
      const welcomeMessage: ChatMessage = {
        id: Date.now().toString(),
        role: 'assistant',
        content: currentJob 
          ? `üëã Hi! I'm here to help you with this ${currentJob.cleaning_type} cleaning job. Ask me anything or use the quick actions below!`
          : `üëã Hi! I'm your PureTask AI Assistant. How can I help you today?`,
        timestamp: new Date()
      };
      setMessages([welcomeMessage]);
    }
  }, []);

  const handleSendMessage = async () => {
    if (!input.trim() || loading) return;

    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      role: 'user',
      content: input,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    setShowQuickActions(false);

    try {
      const response = await aiCleanerChatService.chat([...messages, userMessage], context);
      
      const assistantMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: response,
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      toast.error('Failed to get AI response');
    } finally {
      setLoading(false);
    }
  };

  const handleQuickAction = (question: string) => {
    setInput(question);
    setShowQuickActions(false);
    // Auto-send after a brief delay
    setTimeout(() => {
      handleSendMessage();
    }, 100);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const quickActions = aiCleanerChatService.getQuickActions(currentJob);

  // Minimized view
  if (minimized) {
    return (
      <motion.div
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        className="fixed bottom-4 right-4 z-50"
      >
        <Button
          onClick={onMinimize}
          size="lg"
          className="rounded-full w-16 h-16 bg-info hover:bg-info/90 shadow-lg"
        >
          <Bot className="w-8 h-8" />
        </Button>
        {messages.length > 1 && (
          <Badge className="absolute -top-2 -right-2 bg-error text-white">
            {messages.length - 1}
          </Badge>
        )}
      </motion.div>
    );
  }

  return (
    <Card className="border-info-border shadow-lg">
      <CardHeader className="bg-gradient-to-r from-info to-info/80 text-white">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Bot className="w-6 h-6" />
            <CardTitle className="font-heading text-lg">AI Assistant</CardTitle>
            {currentJob && (
              <Badge variant="secondary" className="ml-2">
                Job #{currentJob.id.slice(0, 8)}
              </Badge>
            )}
          </div>
          {onMinimize && (
            <Button
              onClick={onMinimize}
              variant="ghost"
              size="sm"
              className="text-white hover:bg-white/20"
            >
              <X className="w-4 h-4" />
            </Button>
          )}
        </div>
      </CardHeader>

      <CardContent className="p-0">
        {/* Messages Area */}
        <div className="h-[400px] overflow-y-auto p-4 space-y-4 bg-gray-50">
          <AnimatePresence>
            {messages.map((message) => (
              <motion.div
                key={message.id}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0 }}
                className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`max-w-[80%] rounded-lg p-3 ${
                    message.role === 'user'
                      ? 'bg-info text-white font-body'
                      : 'bg-white border border-gray-200 font-body text-gray-800'
                  }`}
                >
                  {message.role === 'assistant' && (
                    <div className="flex items-center gap-2 mb-1">
                      <Bot className="w-4 h-4 text-info" />
                      <span className="text-xs font-heading font-semibold text-info">
                        PureTask AI
                      </span>
                    </div>
                  )}
                  <p className="whitespace-pre-wrap">{message.content}</p>
                  <span className="text-xs opacity-70 mt-1 block">
                    {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>

          {loading && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="flex justify-start"
            >
              <div className="bg-white border border-gray-200 rounded-lg p-3 flex items-center gap-2">
                <Loader2 className="w-4 h-4 animate-spin text-info" />
                <span className="text-sm font-body text-gray-600">AI is thinking...</span>
              </div>
            </motion.div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {/* Quick Actions */}
        {showQuickActions && messages.length <= 1 && (
          <div className="p-4 bg-white border-t border-gray-200">
            <p className="text-sm font-heading font-semibold text-gray-700 mb-2">
              Quick Actions:
            </p>
            <div className="grid grid-cols-2 gap-2">
              {quickActions.map((action, index) => (
                <Button
                  key={index}
                  onClick={() => handleQuickAction(action.question)}
                  variant="outline"
                  size="sm"
                  className="justify-start text-left h-auto py-2 px-3 font-body"
                >
                  <span className="text-xs">{action.label}</span>
                </Button>
              ))}
            </div>
          </div>
        )}

        {/* Input Area */}
        <div className="p-4 bg-white border-t border-gray-200">
          <div className="flex gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me anything..."
              disabled={loading}
              className="flex-1 border border-gray-300 rounded-lg px-4 py-2 font-body focus:outline-none focus:ring-2 focus:ring-info"
            />
            <Button
              onClick={handleSendMessage}
              disabled={!input.trim() || loading}
              className="bg-info hover:bg-info/90 text-white font-heading"
            >
              {loading ? (
                <Loader2 className="w-5 h-5 animate-spin" />
              ) : (
                <Send className="w-5 h-5" />
              )}
            </Button>
          </div>
          
          {!showQuickActions && (
            <Button
              onClick={() => setShowQuickActions(true)}
              variant="ghost"
              size="sm"
              className="mt-2 text-xs font-body"
            >
              <Sparkles className="w-3 h-3 mr-1" />
              Show quick actions
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

// Floating AI Assistant Button (for when not embedded)
export function FloatingAIAssistant({
  currentJob,
  cleanerId,
  cleanerEmail,
  stats
}: CleanerAIChatAssistantProps) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 20, scale: 0.9 }}
            className="fixed bottom-24 right-4 z-50 w-96"
          >
            <CleanerAIChatAssistant
              currentJob={currentJob}
              cleanerId={cleanerId}
              cleanerEmail={cleanerEmail}
              stats={stats}
              minimized={false}
              onMinimize={() => setIsOpen(false)}
            />
          </motion.div>
        )}
      </AnimatePresence>

      <CleanerAIChatAssistant
        currentJob={currentJob}
        cleanerId={cleanerId}
        cleanerEmail={cleanerEmail}
        stats={stats}
        minimized={!isOpen}
        onMinimize={() => setIsOpen(!isOpen)}
      />
    </>
  );
}

import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Sparkles, Calendar, DollarSign, MapPin, Clock, Check, X, Loader2 } from 'lucide-react';
import { format, parseISO } from 'date-fns';
import { toast } from 'sonner';

export default function CleanerAssistantWidget({ cleanerEmail }) {
  const [pendingRequests, setPendingRequests] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(null);

  useEffect(() => {
    loadPendingRequests();
  }, [cleanerEmail]);

  const loadPendingRequests = async () => {
    try {
      // Get pending booking requests
      const requests = await base44.entities.Booking.filter({
        cleaner_email: cleanerEmail,
        status: { $in: ['awaiting_cleaner_response', 'checking_fallback'] },
        request_expires_at: { $gt: new Date().toISOString() }
      });

      setPendingRequests(requests);

      // Get AI recommendations for each request
      if (requests.length > 0) {
        await getAIRecommendations(requests);
      }
    } catch (error) {
      console.error('Error loading requests:', error);
    }
    setLoading(false);
  };

  const getAIRecommendations = async (requests) => {
    try {
      // Get cleaner profile and existing bookings for context
      const profiles = await base44.entities.CleanerProfile.filter({ user_email: cleanerEmail });
      const profile = profiles[0];

      const existingBookings = await base44.entities.Booking.filter({
        cleaner_email: cleanerEmail,
        status: { $in: ['scheduled', 'accepted'] }
      });

      const prompt = `You are a scheduling assistant for a cleaner. Analyze these booking requests and provide recommendations.

CLEANER INFO:
- Name: ${profile.full_name}
- Tier: ${profile.tier}
- Base Rate: ${profile.base_rate_credits_per_hour} credits/hour
- Preferred Services: ${profile.specialty_tags?.join(', ') || 'General cleaning'}

EXISTING SCHEDULE:
${existingBookings.map(b => `${b.date} ${b.start_time} - ${b.hours}h at ${b.address.split(',')[0]}`).join('\n')}

PENDING REQUESTS:
${requests.map((b, i) => `Request ${i + 1}: ${b.date} ${b.start_time} - ${b.hours}h, ${b.cleaning_type}, ${(b.total_price * 0.8 / 100).toFixed(2)} earnings, Location: ${b.address.split(',')[0]}`).join('\n')}

For EACH request, provide:
1. recommendation: "accept" or "decline" or "reschedule"
2. reason: Brief explanation (schedule fit, earnings, location)
3. suggested_time: If reschedule, suggest better time
4. priority: "high", "medium", or "low"

Return ONLY valid JSON array:
[
  {
    "request_index": 0,
    "recommendation": "accept",
    "reason": "Fits perfectly between morning jobs, great earnings",
    "priority": "high"
  }
]`;

      const response = await base44.integrations.Core.InvokeLLM({
        prompt: prompt,
        response_json_schema: {
          type: "object",
          properties: {
            recommendations: {
              type: "array",
              items: {
                type: "object",
                properties: {
                  request_index: { type: "number" },
                  recommendation: { type: "string" },
                  reason: { type: "string" },
                  suggested_time: { type: "string" },
                  priority: { type: "string" }
                }
              }
            }
          }
        }
      });

      setRecommendations(response.recommendations || []);
    } catch (error) {
      console.error('Error getting AI recommendations:', error);
    }
  };

  const handleApprove = async (booking, recommendation) => {
    setProcessing(booking.id);
    try {
      // Accept the booking
      await base44.entities.Booking.update(booking.id, {
        status: 'accepted',
        cleaner_confirmed: true,
        request_status: 'accepted'
      });

      // Send notification
      await base44.entities.Notification.create({
        recipient_email: booking.client_email,
        type: 'booking_accepted',
        title: '‚úì Booking Accepted!',
        message: `Your cleaning for ${format(parseISO(booking.date), 'MMM d')} has been confirmed`,
        link: '/ClientBookings',
        is_read: false
      });

      toast.success('Booking accepted and added to your schedule!');
      loadPendingRequests();
    } catch (error) {
      console.error('Error accepting booking:', error);
      toast.error('Failed to accept booking');
    }
    setProcessing(null);
  };

  const handleDecline = async (booking) => {
    setProcessing(booking.id);
    try {
      await base44.entities.Booking.update(booking.id, {
        status: 'cleaner_declined',
        request_status: 'declined'
      });

      toast.success('Booking declined');
      loadPendingRequests();
    } catch (error) {
      console.error('Error declining booking:', error);
      toast.error('Failed to decline booking');
    }
    setProcessing(null);
  };

  if (loading) {
    return (
      <Card>
        <CardContent className="py-8">
          <Loader2 className="w-6 h-6 animate-spin mx-auto text-puretask-blue" />
        </CardContent>
      </Card>
    );
  }

  if (pendingRequests.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="font-fredoka flex items-center gap-2">
            <Sparkles className="w-5 h-5 text-puretask-blue" />
            AI Assistant
          </CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-sm text-gray-600 font-verdana">
            No pending requests. I'll notify you when new opportunities arrive! üéØ
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="font-fredoka flex items-center gap-2">
          <Sparkles className="w-5 h-5 text-puretask-blue" />
          AI Assistant - {pendingRequests.length} New Request{pendingRequests.length !== 1 ? 's' : ''}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {pendingRequests.map((booking, idx) => {
          const recommendation = recommendations.find(r => r.request_index === idx);
          const earnings = (booking.total_price * 0.8) / 100;

          return (
            <div
              key={booking.id}
              className={`p-4 rounded-lg border-2 ${
                recommendation?.recommendation === 'accept'
                  ? 'border-green-200 bg-green-50'
                  : recommendation?.recommendation === 'decline'
                  ? 'border-red-200 bg-red-50'
                  : 'border-gray-200 bg-white'
              }`}
            >
              {/* Recommendation Badge */}
              {recommendation && (
                <div className="flex items-center justify-between mb-3">
                  <Badge
                    className={`${
                      recommendation.recommendation === 'accept'
                        ? 'bg-green-600'
                        : recommendation.recommendation === 'decline'
                        ? 'bg-red-600'
                        : 'bg-amber-600'
                    } text-white`}
                  >
                    <Sparkles className="w-3 h-3 mr-1" />
                    AI Suggests: {recommendation.recommendation.toUpperCase()}
                  </Badge>
                  <Badge variant="outline" className={`text-xs ${
                    recommendation.priority === 'high' ? 'border-red-500 text-red-700' :
                    recommendation.priority === 'medium' ? 'border-amber-500 text-amber-700' :
                    'border-gray-400 text-gray-600'
                  }`}>
                    {recommendation.priority} priority
                  </Badge>
                </div>
              )}

              {/* Booking Details */}
              <div className="space-y-2 mb-3">
                <div className="flex items-center gap-2 text-sm">
                  <Calendar className="w-4 h-4 text-gray-500" />
                  <span className="font-fredoka font-semibold">
                    {format(parseISO(booking.date), 'EEE, MMM d')} at {booking.start_time}
                  </span>
                  <Badge variant="outline" className="ml-auto">
                    {booking.cleaning_type}
                  </Badge>
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-700">
                  <Clock className="w-4 h-4 text-gray-500" />
                  <span className="font-verdana">{booking.hours} hours</span>
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-700">
                  <MapPin className="w-4 h-4 text-gray-500" />
                  <span className="font-verdana">{booking.address.split(',')[0]}</span>
                </div>
                <div className="flex items-center gap-2 text-sm">
                  <DollarSign className="w-4 h-4 text-green-600" />
                  <span className="font-fredoka font-semibold text-green-700">
                    ${earnings.toFixed(2)} earnings
                  </span>
                </div>
              </div>

              {/* AI Reasoning */}
              {recommendation && (
                <div className="p-2 bg-white/50 rounded text-xs mb-3">
                  <p className="font-verdana text-gray-700">
                    <span className="font-semibold">AI Analysis:</span> {recommendation.reason}
                  </p>
                </div>
              )}

              {/* Actions */}
              <div className="flex gap-2">
                <Button
                  size="sm"
                  onClick={() => handleApprove(booking, recommendation)}
                  disabled={processing === booking.id}
                  className="flex-1 bg-green-600 hover:bg-green-700 text-white"
                >
                  {processing === booking.id ? (
                    <Loader2 className="w-4 h-4 animate-spin" />
                  ) : (
                    <>
                      <Check className="w-4 h-4 mr-1" />
                      Accept
                    </>
                  )}
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => handleDecline(booking)}
                  disabled={processing === booking.id}
                  className="flex-1 border-red-300 text-red-600 hover:bg-red-50"
                >
                  <X className="w-4 h-4 mr-1" />
                  Decline
                </Button>
              </div>
            </div>
          );
        })}
      </CardContent>
    </Card>
  );
}

// EarningsOptimizationPanel - AI-powered earnings tips
// Personalized recommendations to increase cleaner earnings

import React, { useState, useEffect } from 'react';
import { aiCleanerChatService, CleanerContext } from '@/services/aiCleanerChatService';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  TrendingUp, DollarSign, Target, Clock, Star, Award,
  Zap, Calendar, MapPin, Loader2, Lightbulb, ChevronRight
} from 'lucide-react';
import { motion } from 'framer-motion';

interface EarningsOptimizationPanelProps {
  cleanerId: string;
  cleanerEmail: string;
  stats: {
    totalJobs: number;
    reliabilityScore: number;
    avgRating: number;
    totalEarnings: number;
  };
}

export default function EarningsOptimizationPanel({
  cleanerId,
  cleanerEmail,
  stats
}: EarningsOptimizationPanelProps) {
  const [tips, setTips] = useState<string>('');
  const [scheduleTips, setScheduleTips] = useState<string>('');
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'earnings' | 'schedule'>('earnings');

  useEffect(() => {
    loadOptimizations();
  }, []);

  const loadOptimizations = async () => {
    try {
      setLoading(true);
      const context: CleanerContext = {
        cleanerId,
        cleanerEmail,
        stats
      };

      const [earningsTips, scheduleRecommendations] = await Promise.all([
        aiCleanerChatService.getEarningsOptimization(context),
        aiCleanerChatService.getScheduleRecommendations(context)
      ]);

      setTips(earningsTips);
      setScheduleTips(scheduleRecommendations);
    } catch (error) {
      console.error('Failed to load optimization tips:', error);
    } finally {
      setLoading(false);
    }
  };

  // Calculate potential earnings increase
  const getPotentialIncrease = () => {
    const avgPerJob = stats.totalJobs > 0 ? stats.totalEarnings / stats.totalJobs : 40;
    const reliabilityBonus = stats.reliabilityScore >= 90 ? 1.15 : 1.0;
    const moreJobsPerWeek = 2; // Assume 2 more jobs per week
    const potentialWeekly = avgPerJob * moreJobsPerWeek * reliabilityBonus;
    const potentialMonthly = potentialWeekly * 4;
    
    return {
      weekly: potentialWeekly,
      monthly: potentialMonthly,
      yearly: potentialMonthly * 12
    };
  };

  const potential = getPotentialIncrease();

  if (loading) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <Loader2 className="w-8 h-8 animate-spin text-info mx-auto mb-4" />
          <p className="font-body text-gray-600">AI is analyzing your earnings potential...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="border-success-border">
      <CardHeader className="bg-gradient-to-r from-success-soft to-white">
        <CardTitle className="font-heading text-xl flex items-center gap-2">
          <TrendingUp className="w-6 h-6 text-success" />
          Earnings Optimization
        </CardTitle>
      </CardHeader>

      <CardContent className="p-6 space-y-6">
        {/* Stats Overview */}
        <div className="grid grid-cols-3 gap-4">
          <Card className="bg-success-soft border-success-border">
            <CardContent className="p-4 text-center">
              <DollarSign className="w-6 h-6 text-success mx-auto mb-2" />
              <p className="text-2xl font-heading font-bold text-success">
                ${stats.totalEarnings.toFixed(0)}
              </p>
              <p className="text-xs font-body text-gray-600">Total Earned</p>
            </CardContent>
          </Card>

          <Card className="bg-system-soft border-system-border">
            <CardContent className="p-4 text-center">
              <Award className="w-6 h-6 text-system mx-auto mb-2" />
              <p className="text-2xl font-heading font-bold text-system">
                {stats.reliabilityScore}%
              </p>
              <p className="text-xs font-body text-gray-600">Reliability</p>
            </CardContent>
          </Card>

          <Card className="bg-warning-soft border-warning-border">
            <CardContent className="p-4 text-center">
              <Star className="w-6 h-6 text-warning mx-auto mb-2" />
              <p className="text-2xl font-heading font-bold text-warning">
                {stats.avgRating.toFixed(1)}
              </p>
              <p className="text-xs font-body text-gray-600">Avg Rating</p>
            </CardContent>
          </Card>
        </div>

        {/* Potential Earnings Card */}
        <motion.div
          initial={{ scale: 0.95 }}
          animate={{ scale: 1 }}
          className="bg-gradient-to-br from-success to-success/80 rounded-lg p-6 text-white"
        >
          <div className="flex items-center gap-2 mb-4">
            <Target className="w-6 h-6" />
            <h3 className="font-heading font-bold text-lg">Earnings Potential</h3>
          </div>
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <p className="text-3xl font-heading font-bold">
                +${potential.weekly.toFixed(0)}
              </p>
              <p className="text-sm font-body opacity-90">per week</p>
            </div>
            <div>
              <p className="text-3xl font-heading font-bold">
                +${potential.monthly.toFixed(0)}
              </p>
              <p className="text-sm font-body opacity-90">per month</p>
            </div>
            <div>
              <p className="text-3xl font-heading font-bold">
                +${(potential.yearly / 1000).toFixed(1)}k
              </p>
              <p className="text-sm font-body opacity-90">per year</p>
            </div>
          </div>
          <p className="text-sm font-body mt-4 opacity-90">
            <Lightbulb className="w-4 h-4 inline mr-1" />
            By optimizing your schedule and accepting strategic jobs, you could earn this much more!
          </p>
        </motion.div>

        {/* Tabs */}
        <div className="flex gap-2 border-b">
          <button
            onClick={() => setActiveTab('earnings')}
            className={`flex-1 py-3 px-4 font-heading font-semibold transition-colors ${
              activeTab === 'earnings'
                ? 'text-success border-b-2 border-success'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            <DollarSign className="w-4 h-4 inline mr-2" />
            Earnings Tips
          </button>
          <button
            onClick={() => setActiveTab('schedule')}
            className={`flex-1 py-3 px-4 font-heading font-semibold transition-colors ${
              activeTab === 'schedule'
                ? 'text-success border-b-2 border-success'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            <Calendar className="w-4 h-4 inline mr-2" />
            Schedule Tips
          </button>
        </div>

        {/* Content */}
        <motion.div
          key={activeTab}
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          className="space-y-4"
        >
          {activeTab === 'earnings' && (
            <div className="space-y-4">
              <div className="flex items-start gap-3 p-4 bg-success-soft rounded-lg">
                <Zap className="w-5 h-5 text-success mt-1 flex-shrink-0" />
                <div className="flex-1">
                  <h4 className="font-heading font-semibold text-gray-800 mb-2">
                    AI-Powered Recommendations
                  </h4>
                  <div className="font-body text-gray-700 whitespace-pre-wrap">
                    {tips}
                  </div>
                </div>
              </div>

              {/* Quick Actions */}
              <div className="grid grid-cols-2 gap-3">
                <Button
                  variant="outline"
                  className="justify-start font-body"
                  size="sm"
                >
                  <Star className="w-4 h-4 mr-2 text-warning" />
                  Improve Rating
                </Button>
                <Button
                  variant="outline"
                  className="justify-start font-body"
                  size="sm"
                >
                  <Award className="w-4 h-4 mr-2 text-system" />
                  Boost Reliability
                </Button>
                <Button
                  variant="outline"
                  className="justify-start font-body"
                  size="sm"
                >
                  <Clock className="w-4 h-4 mr-2 text-info" />
                  Work Peak Hours
                </Button>
                <Button
                  variant="outline"
                  className="justify-start font-body"
                  size="sm"
                >
                  <MapPin className="w-4 h-4 mr-2 text-success" />
                  Optimize Routes
                </Button>
              </div>
            </div>
          )}

          {activeTab === 'schedule' && (
            <div className="space-y-4">
              <div className="flex items-start gap-3 p-4 bg-system-soft rounded-lg">
                <Calendar className="w-5 h-5 text-system mt-1 flex-shrink-0" />
                <div className="flex-1">
                  <h4 className="font-heading font-semibold text-gray-800 mb-2">
                    Schedule Optimization
                  </h4>
                  <div className="font-body text-gray-700 whitespace-pre-wrap">
                    {scheduleTips}
                  </div>
                </div>
              </div>

              {/* Best Times */}
              <Card className="bg-gradient-to-r from-info-soft to-white">
                <CardContent className="p-4">
                  <h4 className="font-heading font-semibold mb-3 flex items-center gap-2">
                    <Clock className="w-5 h-5 text-info" />
                    Peak Earning Times
                  </h4>
                  <ul className="space-y-2 font-body text-sm">
                    <li className="flex items-center justify-between">
                      <span>Friday - Sunday mornings</span>
                      <Badge variant="success">High Demand</Badge>
                    </li>
                    <li className="flex items-center justify-between">
                      <span>Move-out cleanings (end of month)</span>
                      <Badge variant="success">High Pay</Badge>
                    </li>
                    <li className="flex items-center justify-between">
                      <span>Weekday afternoons</span>
                      <Badge variant="system">Moderate</Badge>
                    </li>
                  </ul>
                </CardContent>
              </Card>
            </div>
          )}
        </motion.div>

        {/* Refresh Button */}
        <Button
          onClick={loadOptimizations}
          variant="outline"
          className="w-full font-heading"
          disabled={loading}
        >
          {loading ? (
            <Loader2 className="w-4 h-4 mr-2 animate-spin" />
          ) : (
            <ChevronRight className="w-4 h-4 mr-2" />
          )}
          Refresh Recommendations
        </Button>
      </CardContent>
    </Card>
  );
}

// JobOfferRecommendation - AI-powered job offer analysis
// Helps cleaners make informed decisions about accepting jobs

import React, { useState, useEffect } from 'react';
import { aiCleanerChatService } from '@/services/aiCleanerChatService';
import type { JobRecord } from '@/types/cleanerJobTypes';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import {
  TrendingUp, TrendingDown, Minus, DollarSign, Clock, MapPin,
  CheckCircle, AlertTriangle, Zap, Target, Award, Loader2
} from 'lucide-react';
import { motion } from 'framer-motion';

interface JobOfferRecommendationProps {
  job: JobRecord;
  cleanerId: string;
  cleanerEmail: string;
  stats?: {
    totalJobs: number;
    reliabilityScore: number;
    avgRating: number;
    totalEarnings: number;
  };
  onAccept?: () => void;
  onDecline?: () => void;
}

export default function JobOfferRecommendation({
  job,
  cleanerId,
  cleanerEmail,
  stats,
  onAccept,
  onDecline
}: JobOfferRecommendationProps) {
  const [analysis, setAnalysis] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    analyzeOffer();
  }, [job.id]);

  const analyzeOffer = async () => {
    try {
      setLoading(true);
      const result = await aiCleanerChatService.analyzeJobOffer(job, {
        cleanerId,
        cleanerEmail,
        currentJob: job,
        stats
      });
      setAnalysis(result);
    } catch (error) {
      console.error('Failed to analyze job offer:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Card className="border-info-border">
