import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Repeat, Calendar, Info } from 'lucide-react';
import { format, addWeeks, addMonths } from 'date-fns';

export default function RecurringBookingSetup({ bookingTemplate, onGenerateDates, onCancel }) {
  const [frequency, setFrequency] = useState('weekly');
  const [dayOfWeek, setDayOfWeek] = useState('Monday');
  const [occurrences, setOccurrences] = useState(4);
  const [previewDates, setPreviewDates] = useState([]);

  const generatePreviewDates = () => {
    const dates = [];
    const startDate = new Date(bookingTemplate.date);

    for (let i = 0; i < occurrences; i++) {
      let nextDate;
      if (frequency === 'weekly') {
        nextDate = addWeeks(startDate, i);
      } else if (frequency === 'biweekly') {
        nextDate = addWeeks(startDate, i * 2);
      } else if (frequency === 'monthly') {
        nextDate = addMonths(startDate, i);
      }
      dates.push(format(nextDate, 'yyyy-MM-dd'));
    }

    setPreviewDates(dates);
    return dates;
  };

  const handleGenerate = () => {
    const dates = generatePreviewDates();
    onGenerateDates({
      frequency,
      dayOfWeek,
      occurrences,
      dates
    });
  };

  React.useEffect(() => {
    if (occurrences > 0) {
      generatePreviewDates();
    }
  }, [frequency, occurrences, bookingTemplate.date]);

  return (
    <Card className="border-2 border-purple-300 rounded-2xl">
      <CardHeader className="bg-gradient-to-r from-purple-50 to-pink-50 rounded-t-2xl">
        <CardTitle className="font-fredoka text-graphite flex items-center gap-2">
          <Repeat className="w-5 h-5 text-purple-600" />
          Set Up Recurring Bookings
        </CardTitle>
      </CardHeader>
      <CardContent className="p-6 space-y-6">
        <Alert className="border-purple-200 bg-purple-50 rounded-xl">
          <Info className="w-4 h-4 text-purple-600" />
          <AlertDescription className="text-sm text-purple-900 font-verdana">
            Create multiple bookings with the same settings, repeating at regular intervals.
          </AlertDescription>
        </Alert>

        {/* Frequency Selection */}
        <div>
          <Label className="font-fredoka font-medium text-graphite mb-2 block">
            Frequency
          </Label>
          <div className="grid grid-cols-3 gap-3">
            {[
              { value: 'weekly', label: 'Weekly' },
              { value: 'biweekly', label: 'Bi-Weekly' },
              { value: 'monthly', label: 'Monthly' }
            ].map(option => (
              <button
                key={option.value}
                type="button"
                onClick={() => setFrequency(option.value)}
                className={`p-3 rounded-xl text-sm font-fredoka font-semibold transition-all border-2 ${
                  frequency === option.value
                    ? 'border-purple-500 bg-purple-500 text-white shadow-lg'
                    : 'border-gray-300 bg-white text-graphite hover:border-purple-300'
                }`}
              >
                {option.label}
              </button>
            ))}
          </div>
        </div>

        {/* Number of Occurrences */}
        <div>
          <Label className="font-fredoka font-medium text-graphite mb-2 block">
            Number of Bookings
          </Label>
          <Input
            type="number"
            min="1"
            max="52"
            value={occurrences}
            onChange={(e) => setOccurrences(parseInt(e.target.value) || 1)}
            className="font-verdana"
          />
          <p className="text-xs text-gray-500 mt-1 font-verdana">
            Maximum 52 recurring bookings
          </p>
        </div>

        {/* Preview Dates */}
        {previewDates.length > 0 && (
          <div className="bg-gray-50 rounded-xl p-4">
            <p className="text-sm font-fredoka font-semibold text-gray-700 mb-3 flex items-center gap-2">
              <Calendar className="w-4 h-4" />
              Preview ({previewDates.length} bookings)
            </p>
            <div className="max-h-48 overflow-y-auto space-y-1">
              {previewDates.map((date, index) => (
                <div key={index} className="flex items-center justify-between text-sm font-verdana py-1">
                  <span className="text-gray-700">Booking #{index + 1}</span>
                  <Badge variant="outline" className="font-verdana">
                    {format(new Date(date), 'MMM d, yyyy')} at {bookingTemplate.time}
                  </Badge>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Actions */}
        <div className="flex gap-3 pt-4 border-t">
          <Button
            variant="outline"
            onClick={onCancel}
            className="flex-1 rounded-full font-fredoka font-semibold"
          >
            Cancel
          </Button>
          <Button
            onClick={handleGenerate}
            className="flex-1 bg-purple-600 hover:bg-purple-700 text-white rounded-full font-fredoka font-semibold"
          >
            Generate {occurrences} Bookings
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

import React, { useEffect, useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Sparkles, TrendingUp } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

/**
 * Analyzes booking context and suggests relevant bundles
 */
export function analyzeBundleSuggestions({ homeType, squareFeet, hasPets, selectedTasks, bookingHistory = [] }) {
  const suggestions = [];

  // Move-out scenario
  if (selectedTasks.includes('deep_clean') && !selectedTasks.includes('oven')) {
    suggestions.push({
      id: 'moveout',
      name: 'Complete Move-Out Clean',
      reason: 'Perfect for getting your deposit back',
      tasks: ['oven', 'refrigerator', 'interior_walls'],
      savings: 20,
      confidence: 0.9
    });
  }

  // Spring cleaning scenario
  if (selectedTasks.includes('deep_clean') && !selectedTasks.includes('windows')) {
    suggestions.push({
      id: 'spring',
      name: 'Spring Refresh',
      reason: 'Popular combo for seasonal deep clean',
      tasks: ['windows', 'interior_walls'],
      savings: 15,
      confidence: 0.8
    });
  }

  // Kitchen focus
  if (selectedTasks.includes('kitchen') && !selectedTasks.includes('oven') && !selectedTasks.includes('refrigerator')) {
    suggestions.push({
      id: 'kitchen_deep',
      name: 'Kitchen Deep Clean',
      reason: 'Get every inch of your kitchen sparkling',
      tasks: ['oven', 'refrigerator', 'dishes'],
      savings: 15,
      confidence: 0.85
    });
  }

  // Large home bonus
  if (squareFeet > 2000 && !selectedTasks.includes('organizing')) {
    suggestions.push({
      id: 'large_home',
      name: 'Large Home Package',
      reason: 'Extra help for bigger spaces',
      tasks: ['organizing', 'laundry'],
      savings: 10,
      confidence: 0.7
    });
  }

  // Pet owner special
  if (hasPets && !selectedTasks.includes('deep_clean')) {
    suggestions.push({
      id: 'pet_owner',
      name: 'Pet-Friendly Deep Clean',
      reason: 'Remove pet hair and dander thoroughly',
      tasks: ['deep_clean'],
      savings: 15,
      confidence: 0.75
    });
  }

  // Sort by confidence and return top 2
  return suggestions
    .sort((a, b) => b.confidence - a.confidence)
    .slice(0, 2);
}

export default function SmartBundleSuggestions({ 
  homeType, 
  squareFeet, 
  hasPets, 
  selectedTasks,
  appliedBundles,
  onApplyBundle 
}) {
  const [suggestions, setSuggestions] = useState([]);
  const [dismissed, setDismissed] = useState([]);

  useEffect(() => {
    const newSuggestions = analyzeBundleSuggestions({
      homeType,
      squareFeet,
      hasPets,
      selectedTasks
    });

    // Filter out already applied or dismissed
    const filtered = newSuggestions.filter(
      s => !appliedBundles.includes(s.id) && !dismissed.includes(s.id)
    );

    setSuggestions(filtered);
  }, [homeType, squareFeet, hasPets, selectedTasks, appliedBundles, dismissed]);

  if (suggestions.length === 0) return null;

  return (
    <div className="space-y-3">
      <div className="flex items-center gap-2 mb-3">
        <TrendingUp className="w-5 h-5 text-purple-600" />
        <h3 className="font-semibold text-slate-900">Recommended for You</h3>
      </div>

      <AnimatePresence>
        {suggestions.map((suggestion) => (
          <motion.div
            key={suggestion.id}
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 20 }}
            transition={{ duration: 0.3 }}
          >
            <Card className="border-2 border-purple-200 bg-gradient-to-r from-purple-50 to-pink-50 overflow-hidden">
              <CardContent className="p-4">
                <div className="flex items-start justify-between mb-2">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <Sparkles className="w-5 h-5 text-purple-600" />
                      <h4 className="font-bold text-purple-900">{suggestion.name}</h4>
                    </div>
                    <p className="text-sm text-purple-700 mb-2">{suggestion.reason}</p>

                    <div className="flex flex-wrap gap-1 mb-3">
                      {suggestion.tasks.map(task => (
                        <Badge key={task} variant="outline" className="text-xs bg-white border-purple-300 text-purple-800">
                          {task.replace(/_/g, ' ')}
                        </Badge>
                      ))}
                    </div>
                  </div>

                  <Badge className="bg-emerald-500 text-white ml-3 flex-shrink-0">
                    Save ${suggestion.savings}
                  </Badge>
                </div>

                <div className="flex gap-2">
                  <Button
                    size="sm"
                    onClick={() => onApplyBundle(suggestion)}
                    className="flex-1 bg-purple-600 hover:bg-purple-700 text-white"
                  >
                    <Sparkles className="w-4 h-4 mr-1" />
                    Add Bundle
                  </Button>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => setDismissed([...dismissed, suggestion.id])}
                    className="text-slate-600 hover:text-slate-900"
                  >
                    Dismiss
                  </Button>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  );
}

