export function handleRateLimitError(error, setError) {
  if (error.response?.status === 429) {
    setError('Too many requests. Please wait a moment and try again.');
    return true;
  }
  return false;
}

import { format, parseISO, isValid, differenceInHours, differenceInMinutes } from 'date-fns';

/**
 * Safely format a booking date
 */
export function formatBookingDate(date) {
  if (!date) return 'Date not set';

  try {
    const parsedDate = typeof date === 'string' ? parseISO(date) : new Date(date);
    if (isValid(parsedDate)) {
      return format(parsedDate, 'EEEE, MMMM d, yyyy');
    }
  } catch (error) {
    console.error('Error formatting date:', error);
  }
  return String(date);
}

/**
 * Safely format a short date
 */
export function formatShortDate(date) {
  if (!date) return '';

  try {
    const parsedDate = typeof date === 'string' ? parseISO(date) : new Date(date);
    if (isValid(parsedDate)) {
      return format(parsedDate, 'MMM d, yyyy');
    }
  } catch (error) {
    console.error('Error formatting date:', error);
  }
  return String(date);
}

/**
 * Calculate cancellation fee based on time until booking
 */
export function calculateCancellationFee(bookingDate, bookingTime, totalPrice) {
  try {
    const bookingDateTime = new Date(`${bookingDate}T${bookingTime}`);
    const now = new Date();
    const hoursUntil = differenceInHours(bookingDateTime, now);

    if (hoursUntil > 24) {
      return 0; // Free cancellation
    } else if (hoursUntil > 12) {
      return totalPrice * 0.5; // 50% fee
    } else {
      return totalPrice; // 100% fee
    }
  } catch (error) {
    console.error('Error calculating cancellation fee:', error);
    return totalPrice; // Default to full fee on error
  }
}

/**
 * Check if booking can be rescheduled for free
 */
export function canRescheduleForFree(bookingDate, bookingTime) {
  try {
    const bookingDateTime = new Date(`${bookingDate}T${bookingTime}`);
    const now = new Date();
    const hoursUntil = differenceInHours(bookingDateTime, now);

    return hoursUntil > 24;
  } catch (error) {
    return false;
  }
}

/**
 * Check if cleaner is within grace period for check-in
 */
export function isWithinGracePeriod(scheduledDateTime, actualDateTime, gracePeriodMinutes = 15) {
  try {
    const scheduled = new Date(scheduledDateTime);
    const actual = new Date(actualDateTime);
    const diffMinutes = Math.abs(differenceInMinutes(actual, scheduled));

    return diffMinutes <= gracePeriodMinutes;
  } catch (error) {
    return false;
  }
}

import React from 'react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { AlertCircle, RefreshCw } from 'lucide-react';

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error('Error caught by boundary:', error, errorInfo);
    this.setState({ error, errorInfo });
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen flex items-center justify-center p-6 bg-slate-50">
          <div className="max-w-md w-full">
            <Alert className="border-red-200 bg-red-50">
              <AlertCircle className="h-5 w-5 text-red-600" />
              <AlertDescription className="text-red-900">
                <h3 className="font-semibold text-lg mb-2">Something went wrong</h3>
                <p className="text-sm mb-4">
                  We've encountered an unexpected error. Our team has been notified.
                </p>
                <Button 
                  onClick={() => window.location.reload()}
                  className="w-full"
                >
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Reload Page
                </Button>
              </AlertDescription>
            </Alert>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

/**
 * GPS Validation Service
 * Implements anti-spoof measures and distance validation per Section 2.3
 * 
 * Key Rules:
 * - Require device location services enabled
 * - Enforce ‚â§ 250m radius from job address
 * - Log validation attempts for audit
 */

const VALID_RADIUS_METERS = 250;

export const validateGPSLocation = (checkInLocation, jobAddress) => {
  if (!checkInLocation || !checkInLocation.latitude || !checkInLocation.longitude) {
    return {
      valid: false,
      error: 'Location services not enabled. Please enable GPS on your device.',
      code: 'GPS_DISABLED'
    };
  }

  if (!jobAddress || !jobAddress.latitude || !jobAddress.longitude) {
    return {
      valid: false,
      error: 'Job address coordinates not found. Contact support.',
      code: 'INVALID_ADDRESS'
    };
  }

  const distance = calculateDistance(
    checkInLocation.latitude,
    checkInLocation.longitude,
    jobAddress.latitude,
    jobAddress.longitude
  );

  if (distance > VALID_RADIUS_METERS) {
    return {
      valid: false,
      error: `You are ${Math.round(distance)}m from the job location. Please move closer (within 250m) to check in.`,
      distance,
      code: 'OUT_OF_RANGE'
    };
  }

  return {
    valid: true,
    distance,
    message: `GPS verified: ${Math.round(distance)}m from job location`
  };
};

/**
 * Calculate distance between two GPS coordinates using Haversine formula
 * Returns distance in meters
 */
export const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371000; // Earth's radius in meters
  const dLat = toRadians(lat2 - lat1);
  const dLon = toRadians(lon2 - lon1);

  const a =
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(toRadians(lat1)) *
      Math.cos(toRadians(lat2)) *
      Math.sin(dLon / 2) *
      Math.sin(dLon / 2);

  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  const distance = R * c;

  return distance;
};

const toRadians = (degrees) => {
  return degrees * (Math.PI / 180);
};

/**
 * Request device location with high accuracy
 */
export const requestDeviceLocation = () => {
  return new Promise((resolve, reject) => {
    if (!navigator.geolocation) {
      reject({
        valid: false,
        error: 'GPS not supported on this device',
        code: 'GPS_NOT_SUPPORTED'
      });
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        resolve({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          accuracy: position.coords.accuracy,
          timestamp: new Date(position.timestamp).toISOString()
        });
      },
      (error) => {
        let errorMessage = 'Failed to get location';
        let errorCode = 'GPS_ERROR';

        switch (error.code) {
          case error.PERMISSION_DENIED:
            errorMessage = 'Location permission denied. Please enable GPS in your device settings.';
            errorCode = 'GPS_PERMISSION_DENIED';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = 'Location unavailable. Please check your GPS signal.';
            errorCode = 'GPS_UNAVAILABLE';
            break;
          case error.TIMEOUT:
            errorMessage = 'Location request timed out. Please try again.';
            errorCode = 'GPS_TIMEOUT';
            break;
        }

        reject({
          valid: false,
          error: errorMessage,
          code: errorCode
        });
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0
      }
    );
  });
};

/**
 * Format location data for storage
 */
export const formatLocationForStorage = (location, distanceFromJob) => {
  return JSON.stringify({
    latitude: location.latitude,
    longitude: location.longitude,
    accuracy: location.accuracy,
    timestamp: location.timestamp,
    distance_from_job: distanceFromJob
  });
};

/**
 * Parse stored location data
 */
export const parseStoredLocation = (locationString) => {
  try {
    return JSON.parse(locationString);
  } catch (error) {
    return null;
  }
};

export default {
  validateGPSLocation,
  calculateDistance,
  requestDeviceLocation,
  formatLocationForStorage,
  parseStoredLocation,
  VALID_RADIUS_METERS
};

