                <Marker position={[bookingData.latitude, bookingData.longitude]}>
                  <Popup>
                    <div className="text-center p-2">
                      <p className="font-semibold mb-1">Cleaning Location</p>
                      <p className="text-xs text-slate-600">{bookingData.address}</p>
                    </div>
                  </Popup>
                </Marker>
              </MapContainer>
