          <TabsContent value="events">
            <Card className="border-0 shadow-lg">
              <CardHeader>
                <CardTitle>Event Type Distribution</CardTitle>
              </CardHeader>
              <CardContent>
                <ResponsiveContainer width="100%" height={400}>
                  <PieChart>
                    <Pie
                      data={Object.entries(
                        events.reduce((acc, e) => {
                          acc[e.event_type] = (acc[e.event_type] || 0) + 1;
                          return acc;
