              <Wallet className="w-4 h-4 mr-2" />
              Payouts
            </TabsTrigger>
            <TabsTrigger value="pending" className="rounded-full font-fredoka">
              <Clock className="w-4 h-4 mr-2" />
              Pending
            </TabsTrigger>
          </TabsList>

          {/* Overview Tab */}
