import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Loader2, AlertTriangle, CheckCircle } from 'lucide-react';

export default function BookingDataInspector() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [issues, setIssues] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const currentUser = await base44.auth.me();
      if (currentUser.role !== 'admin') {
        alert('Admin access required');
        return;
      }
      setUser(currentUser);
      inspectBookings();
    } catch (error) {
      alert('Please log in as admin');
    }
  };

  const inspectBookings = async () => {
    setLoading(true);
    const foundIssues = [];

    try {
      const allBookings = await base44.entities.Booking.list('-created_date', 100);
      setBookings(allBookings);

      // Check each booking for potential issues
      allBookings.forEach((booking, index) => {
        // Check tasks array
        if (booking.tasks && !Array.isArray(booking.tasks)) {
          foundIssues.push({
            bookingId: booking.id,
            field: 'tasks',
            issue: 'Not an array',
            value: JSON.stringify(booking.tasks)
          });
        }

        // Check task_quantities
        if (booking.task_quantities) {
          try {
            if (typeof booking.task_quantities === 'string') {
              JSON.parse(booking.task_quantities);
            }
          } catch (e) {
            foundIssues.push({
              bookingId: booking.id,
              field: 'task_quantities',
              issue: 'Invalid JSON',
              value: booking.task_quantities
            });
          }
        }

        // Check price_multipliers
        if (booking.price_multipliers && !Array.isArray(booking.price_multipliers)) {
          foundIssues.push({
            bookingId: booking.id,
            field: 'price_multipliers',
            issue: 'Not an array',
            value: JSON.stringify(booking.price_multipliers)
          });
        }

        // Check photos arrays
        if (booking.before_photos && !Array.isArray(booking.before_photos)) {
          foundIssues.push({
            bookingId: booking.id,
            field: 'before_photos',
            issue: 'Not an array',
            value: JSON.stringify(booking.before_photos)
          });
        }

        if (booking.after_photos && !Array.isArray(booking.after_photos)) {
          foundIssues.push({
            bookingId: booking.id,
            field: 'after_photos',
            issue: 'Not an array',
            value: JSON.stringify(booking.after_photos)
          });
        }

        // Check for extremely large fields
        const bookingSize = JSON.stringify(booking).length;
        if (bookingSize > 50000) { // 50KB
          foundIssues.push({
            bookingId: booking.id,
            field: 'overall',
            issue: 'Record too large',
            value: `${(bookingSize / 1024).toFixed(2)} KB`
          });
        }
      });

      setIssues(foundIssues);
    } catch (error) {
      handleError(error, { userMessage: 'Error inspecting bookings:', showToast: false });
      foundIssues.push({
        bookingId: 'N/A',
        field: 'general',
        issue: 'Failed to load bookings',
        value: error.message
      });
      setIssues(foundIssues);
    }

    setLoading(false);
  };

  if (loading) {
    return (
      <div className="min-h-screen p-10 flex items-center justify-center">
        <Loader2 className="w-12 h-12 animate-spin text-blue-500" />
      </div>
    );
  }

  return (
    <div className="min-h-screen p-10">
      <div className="max-w-6xl mx-auto">
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Booking Data Inspector (Admin Only)</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="grid md:grid-cols-3 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <p className="text-sm text-slate-600">Total Bookings</p>
                  <p className="text-2xl font-bold text-blue-600">{bookings.length}</p>
                </div>
                <div className={`p-4 rounded-lg ${issues.length > 0 ? 'bg-red-50' : 'bg-green-50'}`}>
                  <p className="text-sm text-slate-600">Issues Found</p>
                  <p className={`text-2xl font-bold ${issues.length > 0 ? 'text-red-600' : 'text-green-600'}`}>
                    {issues.length}
                  </p>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <p className="text-sm text-slate-600">Healthy Records</p>
                  <p className="text-2xl font-bold text-purple-600">
                    {bookings.length - new Set(issues.map(i => i.bookingId)).size}
                  </p>
                </div>
              </div>

              <Button onClick={inspectBookings} className="w-full">
                Re-run Inspection
              </Button>
            </div>
          </CardContent>
        </Card>

        {issues.length === 0 ? (
          <Alert className="bg-green-50 border-green-200">
            <CheckCircle className="w-4 h-4 text-green-600" />
            <AlertDescription className="text-green-800">
              ‚úÖ All booking records appear healthy! No data issues detected.
            </AlertDescription>
          </Alert>
        ) : (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <AlertTriangle className="w-5 h-5 text-red-500" />
                Data Issues Detected
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {issues.map((issue, idx) => (
                  <div key={idx} className="bg-red-50 p-4 rounded-lg border border-red-200">
                    <div className="flex items-start justify-between mb-2">
                      <div>
                        <p className="font-semibold text-red-900">Booking ID: {issue.bookingId}</p>
                        <p className="text-sm text-red-700">Field: <code className="bg-red-100 px-2 py-1 rounded">{issue.field}</code></p>
                      </div>
                      <span className="text-xs bg-red-200 text-red-800 px-2 py-1 rounded">{issue.issue}</span>
                    </div>
                    <div className="bg-white p-2 rounded border border-red-200 mt-2">
                      <p className="text-xs font-mono text-slate-600 break-all">
                        {issue.value.substring(0, 200)}{issue.value.length > 200 && '...'}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              <Alert className="mt-6 bg-blue-50 border-blue-200">
                <AlertDescription className="text-blue-900">
                  <strong>What to do:</strong>
                  <ol className="list-decimal list-inside mt-2 space-y-1">
                    <li>Copy the problematic Booking IDs above</li>
                    <li>Go to Base44 Dashboard ‚Üí Data ‚Üí Booking</li>
                    <li>Search for each booking by ID</li>
                    <li>Either fix the malformed data or delete the record</li>
                  </ol>
                </AlertDescription>
              </Alert>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { useNavigate, useLocation } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Textarea } from '@/components/ui/textarea';
import {
  Sparkles, Send, MapPin, Calendar, Clock, DollarSign, 
  CheckCircle, ArrowLeft, Loader2, Star, Info, Home, Package, TrendingUp, Award, Briefcase
} from 'lucide-react';
import { motion } from 'framer-motion';

export default function BookingRequest() {
  const navigate = useNavigate();
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const cleanerEmail = searchParams.get('cleaner');

  const [user, setUser] = useState(null);
  const [cleaner, setCleaner] = useState(null);
  const [clientProfile, setClientProfile] = useState(null);
  const [bookingData, setBookingData] = useState(null);
  const [addOns, setAddOns] = useState({});
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [specialRequests, setSpecialRequests] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      // Check auth
      const currentUser = await base44.auth.me();
      if (!currentUser) {
        navigate(createPageUrl('ClientSignup'));
        return;
      }
      setUser(currentUser);

      // Load client profile
      const profiles = await base44.entities.ClientProfile.filter({ user_email: currentUser.email });
      if (profiles.length > 0) {
        setClientProfile(profiles[0]);
      }

      // Load booking data
      const savedBooking = localStorage.getItem('bookingDraft');
      const savedAddOns = localStorage.getItem('bookingAddOns');

      if (!savedBooking || !cleanerEmail) {
        navigate(createPageUrl('Home'));
        return;
      }

      setBookingData(JSON.parse(savedBooking));
      if (savedAddOns) {
        setAddOns(JSON.parse(savedAddOns));
      }

      // Load cleaner profile
      const cleanerProfiles = await base44.entities.CleanerProfile.filter({ user_email: cleanerEmail });
      if (cleanerProfiles.length === 0) {
        alert('Cleaner not found');
        navigate(createPageUrl('MatchedCleaners'));
        return;
      }
      setCleaner(cleanerProfiles[0]);
    } catch (error) {
      handleError(error, { userMessage: 'Error loading data:', showToast: false });
      navigate(createPageUrl('Home'));
    }
    setLoading(false);
  };

  const calculateTotal = () => {
    if (!cleaner || !bookingData) return { credits: 0, usd: 0 };

    const baseRate = cleaner.base_rate_credits_per_hour || 300;
    let addonRate = 0;

    if (bookingData.serviceType === 'deep') {
      addonRate = cleaner.deep_addon_credits_per_hour || 50;
    } else if (bookingData.serviceType === 'moveout') {
      addonRate = cleaner.moveout_addon_credits_per_hour || 50;
    }

    const hourlyTotal = (baseRate + addonRate) * bookingData.recommendedHours;

    // Calculate add-ons (using cleaner's pricing if available)
    let addOnsTotal = 0;
    if (cleaner.additional_service_pricing) {
      Object.keys(addOns).forEach(key => {
        const price = cleaner.additional_service_pricing[key] || 0;
        addOnsTotal += price * addOns[key];
      });
    }

    const totalCredits = hourlyTotal + addOnsTotal;
    return {
      credits: Math.round(totalCredits),
      usd: (totalCredits / 10).toFixed(2),
      hourlyTotal: Math.round(hourlyTotal),
      addOnsTotal: Math.round(addOnsTotal)
    };
  };

  const handleSubmitRequest = async () => {
    setSubmitting(true);

    try {
      const estimate = calculateTotal();

      // Create booking in "awaiting_cleaner_response" status
      const newBooking = await base44.entities.Booking.create({
        client_email: user.email,
        cleaner_email: cleanerEmail,
        cleaning_type: bookingData.serviceType,
        date: bookingData.date,
        start_time: bookingData.time,
        hours: bookingData.recommendedHours,
        estimated_hours: bookingData.recommendedHours,
        address: bookingData.address,
        latitude: bookingData.latitude,
        longitude: bookingData.longitude,
        bedrooms: parseInt(bookingData.bedrooms),
        bathrooms: parseFloat(bookingData.bathrooms),
        square_feet: bookingData.squareFeet,
        additional_services: addOns,
        notes: specialRequests,
        total_price: parseFloat(estimate.usd),
        snapshot_base_rate_cph: cleaner.base_rate_credits_per_hour || 300,
        snapshot_selected_addon_cph: bookingData.serviceType === 'deep' ? (cleaner.deep_addon_credits_per_hour || 50) :
                                     bookingData.serviceType === 'moveout' ? (cleaner.moveout_addon_credits_per_hour || 50) : 0,
        snapshot_total_rate_cph: (cleaner.base_rate_credits_per_hour || 300) + 
                                 (bookingData.serviceType === 'deep' ? (cleaner.deep_addon_credits_per_hour || 50) :
                                  bookingData.serviceType === 'moveout' ? (cleaner.moveout_addon_credits_per_hour || 50) : 0),
        snapshot_additional_service_prices: cleaner.additional_service_pricing || {},
        additional_services_cost_credits: estimate.addOnsTotal,
        escrow_credits_reserved: estimate.credits,
        payout_percentage_at_accept: cleaner.payout_percentage || 0.80,
        status: 'awaiting_cleaner_response',
        request_sent_at: new Date().toISOString(),
        request_expires_at: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString() // 24 hours
      });

      // Clear localStorage
      localStorage.removeItem('bookingDraft');
      localStorage.removeItem('bookingAddOns');
      localStorage.removeItem('selectedCleaner');

      // Navigate to confirmation page
      navigate(createPageUrl(`BookingRequestSent?booking=${newBooking.id}`));
    } catch (error) {
      handleError(error, { userMessage: 'Error submitting booking request:', showToast: false });
      alert('Failed to send booking request. Please try again.');
    }
    setSubmitting(false);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-soft-cloud flex items-center justify-center">
