import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { useNavigate, useLocation } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import {
  CheckCircle, Clock, Bell, Home, Loader2, Sparkles, MessageSquare, AlertTriangle
} from 'lucide-react';
import { motion } from 'framer-motion';

export default function BookingRequestSent() {
  const navigate = useNavigate();
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const bookingId = searchParams.get('booking');

  const [booking, setBooking] = useState(null);
  const [cleaner, setCleaner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [pollingInterval, setPollingInterval] = useState(null);

  useEffect(() => {
    if (!bookingId) {
      navigate(createPageUrl('ClientDashboard'));
      return;
    }
    loadBooking();

    // Poll for updates every 10 seconds
    const interval = setInterval(loadBooking, 10000);
    setPollingInterval(interval);

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [bookingId]);

  const loadBooking = async () => {
    try {
      const bookings = await base44.entities.Booking.filter({ id: bookingId });
      if (bookings.length === 0) {
        navigate(createPageUrl('ClientDashboard'));
        return;
      }

      const bookingData = bookings[0];
      setBooking(bookingData);

      // Load cleaner info
      const cleanerProfiles = await base44.entities.CleanerProfile.filter({ 
        user_email: bookingData.cleaner_email 
      });
      if (cleanerProfiles.length > 0) {
        setCleaner(cleanerProfiles[0]);
      }

      // If status changed to accepted, redirect to payment
      if (bookingData.status === 'accepted' || bookingData.status === 'payment_hold') {
        navigate(createPageUrl(`PaymentCheckout?booking=${bookingData.id}`));
      }

      // If declined, show alert
      if (bookingData.status === 'cleaner_declined') {
        // Will show declined state in UI
      }
    } catch (error) {
      handleError(error, { userMessage: 'Error loading booking:', showToast: false });
    }
    setLoading(false);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-soft-cloud flex items-center justify-center">
        <Loader2 className="w-16 h-16 animate-spin text-puretask-blue" />
      </div>
    );
  }

  const isDeclined = booking?.status === 'cleaner_declined';
  const timeLeft = booking?.request_expires_at 
    ? Math.max(0, Math.round((new Date(booking.request_expires_at) - new Date()) / 1000 / 60 / 60)) 
    : 24;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-cyan-50 p-6 flex items-center justify-center">
      <div className="max-w-3xl w-full">
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          {!isDeclined ? (
            <>
              {/* Success State */}
              <div className="text-center mb-8">
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ type: "spring", duration: 0.6 }}
                  className="w-24 h-24 brand-gradient rounded-full flex items-center justify-center mx-auto mb-6 shadow-2xl"
                >
                  <CheckCircle className="w-14 h-14 text-white" />
                </motion.div>
                <h1 className="text-4xl font-fredoka font-bold text-graphite mb-3">
                  Request Sent Successfully! üéâ
                </h1>
                <p className="text-lg text-gray-600 font-verdana">
                  We've notified {cleaner?.full_name} about your booking
                </p>
              </div>

              {/* Status Card */}
              <Card className="border-0 shadow-2xl rounded-2xl mb-6 overflow-hidden">
                <div className="bg-gradient-to-r from-amber-500 to-orange-500 p-6 text-white">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <Clock className="w-8 h-8" />
                      <div>
                        <p className="font-fredoka font-bold text-xl">Awaiting Response</p>
                        <p className="text-sm opacity-90 font-verdana">{timeLeft} hours remaining</p>
                      </div>
                    </div>
                    <motion.div
                      animate={{ scale: [1, 1.1, 1] }}
                      transition={{ repeat: Infinity, duration: 2 }}
                    >
                      <Bell className="w-8 h-8" />
                    </motion.div>
                  </div>
                </div>

                <CardContent className="p-6">
                  <Alert className="border-blue-200 bg-blue-50 rounded-xl">
                    <Info className="w-4 h-4 text-puretask-blue" />
                    <AlertDescription className="font-verdana text-gray-700">
                      <strong>{cleaner?.full_name}</strong> has 24 hours to respond to your request. We'll notify you immediately via email and SMS once they accept!
                    </AlertDescription>
                  </Alert>

                  <div className="mt-6 space-y-3">
                    <div className="flex items-center gap-3">
                      <div className="w-8 h-8 bg-green-100 rounded-full flex items-center justify-center">
                        <CheckCircle className="w-5 h-5 text-fresh-mint" />
                      </div>
                      <span className="font-verdana text-gray-700">Request sent to cleaner</span>
                    </div>
                    <div className="flex items-center gap-3 opacity-50">
                      <div className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center">
                        <Clock className="w-5 h-5 text-gray-400" />
                      </div>
                      <span className="font-verdana text-gray-500">Waiting for acceptance...</span>
                    </div>
                    <div className="flex items-center gap-3 opacity-30">
                      <div className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center">
                        <DollarSign className="w-5 h-5 text-gray-400" />
                      </div>
                      <span className="font-verdana text-gray-400">Payment processing</span>
                    </div>
                    <div className="flex items-center gap-3 opacity-30">
                      <div className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center">
                        <Sparkles className="w-5 h-5 text-gray-400" />
                      </div>
                      <span className="font-verdana text-gray-400">Booking confirmed</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </>
          ) : (
            <>
              {/* Declined State */}
              <div className="text-center mb-8">
                <div className="w-24 h-24 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-6">
                  <AlertTriangle className="w-14 h-14 text-red-600" />
                </div>
                <h1 className="text-4xl font-fredoka font-bold text-graphite mb-3">
                  Request Declined
                </h1>
                <p className="text-lg text-gray-600 font-verdana">
                  {cleaner?.full_name} is unable to accept this booking
                </p>
              </div>

              <Card className="border-0 shadow-2xl rounded-2xl mb-6">
                <CardContent className="p-8 text-center">
                  <p className="font-verdana text-gray-700 mb-6">
                    Don't worry! We have many other great cleaners available for your date and time.
                  </p>
                  <Button
                    onClick={() => navigate(createPageUrl('MatchedCleaners'))}
                    className="brand-gradient text-white rounded-full font-fredoka font-bold px-8 py-6"
                  >
                    View Other Available Cleaners
                  </Button>
                </CardContent>
              </Card>
            </>
          )}

          {/* Action Buttons */}
          <div className="flex flex-col gap-4">
            <Button
              onClick={() => navigate(createPageUrl('ClientDashboard'))}
              variant="outline"
              size="lg"
              className="rounded-full font-fredoka font-semibold border-2"
            >
              <Home className="w-5 h-5 mr-2" />
              Go to Dashboard
            </Button>

            {booking && !isDeclined && (
              <Button
                onClick={() => navigate(createPageUrl(`ChatThread?booking=${booking.id}`))}
                variant="outline"
                size="lg"
                className="rounded-full font-fredoka font-semibold border-2"
              >
                <MessageSquare className="w-5 h-5 mr-2" />
                Message {cleaner?.full_name}
              </Button>
            )}
          </div>

          {/* Auto-refresh indicator */}
          {!isDeclined && (
            <p className="text-center text-sm text-gray-500 font-verdana mt-6">
              ‚ü≥ Checking for updates automatically...
            </p>
          )}
        </motion.div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { useNavigate } from 'react-router-dom';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { AlertCircle } from 'lucide-react';
import EnhancedCleanerCard from '../components/cleaner/EnhancedCleanerCard';
import StickyFiltersBar from '@/components/cleaner/StickyFiltersBar';
import AdvancedFiltersPanel from '@/components/cleaner/AdvancedFiltersPanel';
import QuickCompareDrawer from '@/components/cleaner/QuickCompareDrawer';
import { motion, AnimatePresence } from 'framer-motion';

const SkeletonCleanerCard = () => (
  <div className="rounded-3xl shadow-xl h-full flex flex-col animate-pulse bg-gray-100">
    <div className="aspect-video bg-gray-300 rounded-t-3xl"></div>
    <div className="p-6 space-y-4">
      <div className="h-6 bg-gray-300 rounded w-2/3"></div>
      <div className="h-4 bg-gray-200 rounded w-full"></div>
      <div className="h-4 bg-gray-200 rounded w-5/6"></div>
      <div className="grid grid-cols-2 gap-3">
        <div className="h-10 bg-gray-200 rounded-full"></div>
        <div className="h-10 bg-gray-200 rounded-full"></div>
      </div>
    </div>
  </div>
);

export default function BrowseCleaners() {
  const navigate = useNavigate();
  const [allCleaners, setAllCleaners] = useState([]);
  const [filteredCleaners, setFilteredCleaners] = useState([]);
  const [blockedCleaners, setBlockedCleaners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);
  const [loadError, setLoadError] = useState('');
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);
  const [pinnedCleaners, setPinnedCleaners] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const cleanersPerPage = 5;

  const [selectedTier, setSelectedTier] = useState('all');
  const [filters, setFilters] = useState({
    search: '',
    sortBy: 'tier_then_reliability',
    minPrice: 0,
    maxPrice: 100,
    minRating: 0,
    minReliability: 0,
    availability: 'all',
    cleaningType: 'all',
    tier: 'all'
  });

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    // Auto-apply when data loads or blocked list changes
    applyFilters();
  }, [allCleaners, blockedCleaners, selectedTier]);

  const loadData = async () => {
    setLoading(true);
    setLoadError('');
    try {
      const profiles = await base44.entities.CleanerProfile.filter(
        { is_active: true },
        '-reliability_score',
        500
      );

      if (profiles.length === 0) {
        setLoadError('No active cleaners found. Please check back later.');
        setLoading(false);
        return;
      }

      setAllCleaners(profiles);

      try {
        const user = await base44.auth.me();
        setCurrentUser(user);

        if (user.user_type === 'client') {
          const blocked = await base44.entities.BlockedCleaner.filter({ client_email: user.email });
          setBlockedCleaners(blocked.map(b => b.cleaner_email));
        }
      } catch (error) {
        setCurrentUser(null);
      }
    } catch (error) {
      handleError(error, { userMessage: 'Error loading cleaners:', showToast: false });
      setLoadError('Failed to load cleaners. Please refresh the page.');
    }
    setLoading(false);
  };

  const applyFilters = () => {
    let result = [...allCleaners];

    if (blockedCleaners.length > 0) {
      result = result.filter(c => !blockedCleaners.includes(c.user_email));
    }

    if (filters.search.trim()) {
      const searchLower = filters.search.toLowerCase();
      result = result.filter(c => 
        c.full_name?.toLowerCase().includes(searchLower) ||
        c.bio?.toLowerCase().includes(searchLower) ||
        c.service_locations?.some(loc => loc.toLowerCase().includes(searchLower))
      );
    }

    if (filters.minRating > 0) {
      result = result.filter(c => (c.average_rating || 0) >= filters.minRating);
    }

    if (filters.minReliability > 0) {
      result = result.filter(c => (c.reliability_score || 75) >= filters.minReliability);
    }

    result = result.filter(c => {
      const effectiveRate = (c.base_rate_credits_per_hour || c.hourly_rate || 300) / 10;
      return effectiveRate >= filters.minPrice && effectiveRate <= filters.maxPrice;
    });

    if (selectedTier !== 'all') {
      result = result.filter(c => c.tier === selectedTier);
    } else if (filters.tier !== 'all') {
      result = result.filter(c => c.tier === filters.tier);
    }

    // Sort based on selected option
    switch (filters.sortBy) {
      case 'top_rated':
        result.sort((a, b) => (b.average_rating || 0) - (a.average_rating || 0));
        break;
      case 'most_reliable':
        result.sort((a, b) => (b.reliability_score || 75) - (a.reliability_score || 75));
        break;
      case 'lowest_price':
        result.sort((a, b) => {
          const aRate = (a.base_rate_credits_per_hour || 300) / 10;
          const bRate = (b.base_rate_credits_per_hour || 300) / 10;
          return aRate - bRate;
        });
        break;
      case 'tier_then_reliability':
        // Sort by tier first, then reliability within each tier
        const tierOrder = { 'Elite': 0, 'Pro': 1, 'Semi Pro': 2, 'Developing': 3 };
        result.sort((a, b) => {
          const aTier = tierOrder[a.tier] ?? 4;
          const bTier = tierOrder[b.tier] ?? 4;
          if (aTier !== bTier) return aTier - bTier;
          return (b.reliability_score || 75) - (a.reliability_score || 75);
        });
        break;
      case 'recommended':
      default:
        result.sort((a, b) => {
          const aScore = ((a.reliability_score || 75) * 0.6) + ((a.average_rating || 5) * 20 * 0.4);
          const bScore = ((b.reliability_score || 75) * 0.6) + ((b.average_rating || 5) * 20 * 0.4);
          return bScore - aScore;
        });
    }

    setFilteredCleaners(result);
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const handleSearch = () => {
    applyFilters();
    setCurrentPage(1);
  };

  const resetFilters = () => {
    setFilters({
      search: '',
      sortBy: 'tier_then_reliability',
      minPrice: 0,
      maxPrice: 100,
      minRating: 0,
      minReliability: 0,
      availability: 'all',
      cleaningType: 'all',
      tier: 'all'
    });
  };

  const handlePinCleaner = (cleaner) => {
    setPinnedCleaners(prev => {
      const isAlreadyPinned = prev.some(c => c.id === cleaner.id);
      if (isAlreadyPinned) {
        return prev.filter(c => c.id !== cleaner.id);
      }
      if (prev.length >= 3) {
        return prev;
      }
      return [...prev, cleaner];
    });
  };

  const handleRemovePin = (cleanerId) => {
    setPinnedCleaners(prev => prev.filter(c => c.id !== cleanerId));
  };

  const handleClearPins = () => {
    setPinnedCleaners([]);
  };

  const allLocations = [...new Set(allCleaners.flatMap(c => c.service_locations || []))].sort();

  const indexOfLastCleaner = currentPage * cleanersPerPage;
  const indexOfFirstCleaner = indexOfLastCleaner - cleanersPerPage;
  const currentCleaners = filteredCleaners.slice(indexOfFirstCleaner, indexOfLastCleaner);
  const totalPages = Math.ceil(filteredCleaners.length / cleanersPerPage);

  const goToNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
      window.scrollTo({ top: 200, behavior: 'smooth' });
    }
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
      window.scrollTo({ top: 200, behavior: 'smooth' });
    }
  };



  if (loading) {
    return (
      <div className="min-h-screen bg-soft-cloud p-6 lg:p-10">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <h1 className="text-5xl font-fredoka font-bold text-graphite mb-2">Find Your Perfect Cleaner</h1>
            <p className="text-xl text-gray-600 font-verdana">Loading verified professionals...</p>
          </div>
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, i) => <SkeletonCleanerCard key={i} />)}
          </div>
        </div>
      </div>
    );
  }

  if (loadError || allCleaners.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-soft-cloud p-6">
        <Card className="max-w-md rounded-3xl bg-white shadow-2xl border-0">
          <CardContent className="p-8 text-center">
            <AlertCircle className="w-16 h-16 text-red-500 mx-auto mb-4" />
            <h2 className="text-2xl font-fredoka font-bold text-graphite mb-2">
              {loadError || 'No Cleaners Available'}
            </h2>
            <p className="text-gray-600 mb-6 font-verdana">Please check back soon or try refreshing.</p>
            <Button onClick={loadData} className="brand-gradient text-white rounded-full font-fredoka">
              Try Again
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-gray-50 to-slate-100 pb-24 sm:pb-32">
      {/* Header */}
      <div className="bg-white border-b border-slate-200 shadow-sm py-6 sm:py-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-8">
          <motion.div initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }}>
            <h1 className="text-3xl sm:text-4xl lg:text-5xl font-fredoka font-bold text-slate-900 mb-2">
              Professional Cleaners
            </h1>
            <p className="text-base sm:text-lg text-slate-600 font-verdana">
              {allCleaners.length} verified professionals ‚Ä¢ Sorted by tier & reliability
            </p>
          </motion.div>
        </div>
      </div>

      {/* Sticky Filters Bar */}
      <StickyFiltersBar
        filters={filters}
        onFilterChange={handleFilterChange}
        onResetFilters={resetFilters}
        onSearch={handleSearch}
        showAdvanced={showAdvancedFilters}
        setShowAdvanced={setShowAdvancedFilters}
        allLocations={allLocations}
      />

      {/* Advanced Filters Panel */}
      <AnimatePresence>
        {showAdvancedFilters && (
          <AdvancedFiltersPanel
            filters={filters}
            onFilterChange={handleFilterChange}
            onClose={() => setShowAdvancedFilters(false)}
            allLocations={allLocations}
          />
        )}
      </AnimatePresence>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-8 py-6 sm:py-10">
        {/* Tier Selection */}
        <div className="mb-8">
          <h2 className="text-xl font-fredoka font-bold text-slate-900 mb-4">Select Cleaner Tier</h2>
          <div className="grid grid-cols-2 sm:grid-cols-5 gap-3 sm:gap-4">
            <button
              onClick={() => setSelectedTier('all')}
              className={`p-4 rounded-xl border-2 transition-all ${
                selectedTier === 'all'
                  ? 'bg-slate-900 border-slate-900 text-white shadow-lg'
                  : 'bg-white border-slate-200 hover:border-slate-300 text-slate-700'
              }`}
            >
              <p className="font-fredoka font-bold text-base mb-1">All Tiers</p>
              <p className="text-xs opacity-80">{allCleaners.length} cleaners</p>
            </button>

            <button
              onClick={() => setSelectedTier('Elite')}
              className={`p-4 rounded-xl border-2 transition-all ${
                selectedTier === 'Elite'
                  ? 'bg-gradient-to-r from-purple-600 to-purple-700 border-purple-600 text-white shadow-lg'
                  : 'bg-white border-purple-300 hover:bg-purple-50 text-slate-700'
              }`}
            >
              <p className="font-fredoka font-bold text-base mb-1">üëë Elite</p>
              <p className="text-xs opacity-80">{allCleaners.filter(c => c.tier === 'Elite').length} cleaners</p>
            </button>

            <button
              onClick={() => setSelectedTier('Pro')}
              className={`p-4 rounded-xl border-2 transition-all ${
                selectedTier === 'Pro'
                  ? 'bg-gradient-to-r from-blue-600 to-blue-700 border-blue-600 text-white shadow-lg'
                  : 'bg-white border-blue-300 hover:bg-blue-50 text-slate-700'
              }`}
            >
              <p className="font-fredoka font-bold text-base mb-1">‚≠ê Pro</p>
              <p className="text-xs opacity-80">{allCleaners.filter(c => c.tier === 'Pro').length} cleaners</p>
            </button>

            <button
              onClick={() => setSelectedTier('Semi Pro')}
              className={`p-4 rounded-xl border-2 transition-all ${
                selectedTier === 'Semi Pro'
                  ? 'bg-gradient-to-r from-emerald-600 to-emerald-700 border-emerald-600 text-white shadow-lg'
                  : 'bg-white border-emerald-300 hover:bg-emerald-50 text-slate-700'
              }`}
            >
              <p className="font-fredoka font-bold text-base mb-1">üí´ Semi Pro</p>
              <p className="text-xs opacity-80">{allCleaners.filter(c => c.tier === 'Semi Pro').length} cleaners</p>
            </button>

            <button
              onClick={() => setSelectedTier('Developing')}
              className={`p-4 rounded-xl border-2 transition-all ${
                selectedTier === 'Developing'
                  ? 'bg-gradient-to-r from-slate-600 to-slate-700 border-slate-600 text-white shadow-lg'
                  : 'bg-white border-slate-300 hover:bg-slate-50 text-slate-700'
              }`}
            >
              <p className="font-fredoka font-bold text-base mb-1">üåü Developing</p>
              <p className="text-xs opacity-80">{allCleaners.filter(c => c.tier === 'Developing').length} cleaners</p>
            </button>
          </div>
        </div>

        {/* Results Summary */}
        <div className="mb-6 flex items-center justify-between flex-wrap gap-3">
          <p className="text-sm sm:text-base text-slate-700 font-verdana">
            <span className="font-fredoka font-bold text-slate-900 text-lg sm:text-xl">
              {filteredCleaners.length}
            </span>
            {' '}professional{filteredCleaners.length !== 1 ? 's' : ''} available
            {totalPages > 1 && (
              <span className="text-slate-500 ml-2 text-xs sm:text-sm">‚Ä¢ Page {currentPage} of {totalPages}</span>
            )}
          </p>
        </div>

        {/* Cleaners List (Professional Grid) */}
        {currentCleaners.length > 0 ? (
          <>
            <motion.div 
              className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4 sm:gap-5 mb-8"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              {currentCleaners.map(cleaner => (
                <EnhancedCleanerCard
                  key={cleaner.id}
                  cleaner={cleaner}
                  onPin={handlePinCleaner}
                  isPinned={pinnedCleaners.some(c => c.id === cleaner.id)}
                />
              ))}
            </motion.div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="flex items-center justify-center gap-3 sm:gap-4 flex-wrap">
                <Button
                  onClick={goToPreviousPage}
                  disabled={currentPage === 1}
                  variant="outline"
                  className="rounded-lg font-fredoka font-semibold px-6 sm:px-8 h-11 border border-slate-300 disabled:opacity-40 text-sm hover:bg-slate-50"
                >
                  Previous
                </Button>
                <span className="font-verdana text-slate-700 font-medium text-sm bg-white px-4 py-2 rounded-lg border border-slate-200">
                  {currentPage} / {totalPages}
                </span>
                <Button
                  onClick={goToNextPage}
                  disabled={currentPage === totalPages}
                  className="rounded-lg font-fredoka font-semibold px-6 sm:px-8 h-11 bg-slate-900 text-white hover:bg-slate-800 text-sm"
                >
                  Next
                </Button>
              </div>
            )}
          </>
        ) : (
          <Card className="border-0 shadow-xl rounded-3xl">
            <CardContent className="p-12 text-center">
              <AlertCircle className="w-16 h-16 text-gray-300 mx-auto mb-4" />
              <h3 className="text-2xl font-fredoka font-bold text-graphite mb-2">
                No cleaners match your filters
              </h3>
              <p className="text-gray-600 mb-6 font-verdana">
                Try adjusting your search criteria
              </p>
              <Button 
                onClick={resetFilters} 
                className="brand-gradient text-white rounded-full font-fredoka font-semibold px-8"
              >
                Reset All Filters
              </Button>
            </CardContent>
          </Card>
        )}
      </div>

      {/* Quick Compare Drawer */}
      <QuickCompareDrawer
        pinnedCleaners={pinnedCleaners}
        onRemove={handleRemovePin}
        onClear={handleClearPins}
      />
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { useNavigate } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { base44 } from '@/api/base44Client';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { DollarSign, Star, Clock, Camera, Target, CheckCircle, BarChart3, Loader2
} from 'lucide-react';
import { motion } from 'framer-motion';
import {
  LineChart, Line, PieChart, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

export default function CleanerAnalytics() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const currentUser = await base44.auth.me();
      if (!currentUser || currentUser.user_type !== 'cleaner') {
        navigate(createPageUrl('Home'));
        return;
      }
      setUser(currentUser);

      const profiles = await base44.entities.CleanerProfile.filter({ user_email: currentUser.email });
      if (profiles.length > 0) {
        setProfile(profiles[0]);
      }

      const userBookings = await base44.entities.Booking.filter({ 
        cleaner_email: currentUser.email 
      }, '-date');
      setBookings(userBookings);
    } catch (error) {
      handleError(error, { userMessage: 'Error loading analytics:', showToast: false });
    }
    setLoading(false);
  };

  const calculateStats = () => {
    const completed = bookings.filter(b => b.status === 'completed');
    const totalEarnings = completed.reduce((sum, b) => sum + (b.total_price * 0.85), 0);
    const avgRating = profile?.average_rating || 5.0;

    const earningsByMonth = {};
    completed.forEach(b => {
      const month = new Date(b.date).toLocaleDateString('en-US', { month: 'short', year: 'numeric' });
      earningsByMonth[month] = (earningsByMonth[month] || 0) + (b.total_price * 0.85);
    });

    const monthlyData = Object.entries(earningsByMonth)
      .map(([month, earnings]) => ({
        month,
        earnings: Math.round(earnings * 10),
        usd: earnings
      }))
      .slice(-6);

    const serviceTypes = {
      basic: completed.filter(b => b.cleaning_type === 'basic').length,
      deep: completed.filter(b => b.cleaning_type === 'deep').length,
      moveout: completed.filter(b => b.cleaning_type === 'moveout').length
    };

    return {
      totalJobs: completed.length,
      totalEarnings: Math.round(totalEarnings * 10),
      avgRating,
      monthlyData,
      serviceTypes,
      reliabilityScore: profile?.reliability_score || 75
    };
  };

  const stats = calculateStats();

  const COLORS = ['#0078FF', '#00D4FF', '#28C76F', '#FFA500'];

  const pieData = [
    { name: 'Basic', value: stats.serviceTypes.basic },
    { name: 'Deep', value: stats.serviceTypes.deep },
    { name: 'Move-Out', value: stats.serviceTypes.moveout }
  ].filter(d => d.value > 0);

  if (loading) {
    return (
      <div className="min-h-screen bg-soft-cloud flex items-center justify-center">
        <Loader2 className="w-12 h-12 animate-spin text-puretask-blue" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-soft-cloud p-6 lg:p-10">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-fredoka font-bold text-graphite mb-2">Analytics Dashboard</h1>
          <p className="text-lg text-gray-600 font-verdana">Track your performance and earnings</p>
        </div>

        {/* Quick Stats */}
        <div className="grid md:grid-cols-4 gap-6 mb-8">
          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.1 }}>
            <Card className="border-0 shadow-lg hover:shadow-xl transition-all bg-gradient-to-br from-blue-50 to-white rounded-2xl">
              <CardContent className="p-6 text-center">
                <BarChart3 className="w-10 h-10 text-puretask-blue mx-auto mb-3" />
                <p className="text-3xl font-fredoka font-bold text-graphite mb-1">{stats.totalJobs}</p>
                <p className="text-sm text-gray-600 font-verdana">Completed Jobs</p>
              </CardContent>
            </Card>
          </motion.div>

