import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { Link, useNavigate } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { base44 } from '@/api/base44Client';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Slider } from '@/components/ui/slider';
import { Sparkles, Shield, Clock, Star, CheckCircle, TrendingUp, Users, Camera, Calendar, Briefcase, Home as HomeIcon, Heart, ArrowRight, Repeat } from 'lucide-react';
import { motion } from 'framer-motion';
import ImageCarousel from '@/components/home/ImageCarousel';
import AddressAutocomplete from '@/components/address/AddressAutocomplete';

export default function Home() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [bookingData, setBookingData] = useState({
    address: '',
    latitude: null,
    longitude: null,
    serviceType: 'basic',
    bedrooms: '2',
    bathrooms: '2',
    squareFeet: 1500,
    recommendedHours: 3,
    date: '',
    time: ''
  });

  useEffect(() => {
    let isMounted = true;

    const initialize = async () => {
      try {
        if (isMounted) {
          await checkUser();
          // Load draft from localStorage if exists
          const savedDraft = localStorage.getItem('bookingDraft');
          if (savedDraft && isMounted) {
            setBookingData(JSON.parse(savedDraft));
          }
        }
      } catch (error) {
        if (isMounted) {
          handleError(error, { userMessage: 'Error loading home:', showToast: false });
        }
      }
    };

    initialize();

    return () => {
      isMounted = false;
    };
  }, []); // Empty array - only run once on mount

  const checkUser = async () => {
    try {
      const currentUser = await base44.auth.me().catch(() => null);
      setUser(currentUser);
    } catch (error) {
      // User not logged in - this is fine for Home page
      setUser(null);
    }
  };

  const handleBooking = (e) => {
    e.preventDefault();

    if (!bookingData.address || !bookingData.latitude || !bookingData.longitude) {
      alert('Please select a valid address from the dropdown suggestions');
      return;
    }

    if (!bookingData.date || !bookingData.time) {
      alert('Please select both date and time');
      return;
    }

    // Save booking data to localStorage
    localStorage.setItem('bookingDraft', JSON.stringify(bookingData));

