                    <p className="text-sm text-slate-600 mb-3">
                      Cancel your subscription. This cannot be undone and you'll lose your discount.
                    </p>
                    <Button
                      onClick={handleCancel}
                      variant="outline"
                      className="w-full border-red-500 text-red-600 hover:bg-red-50"
                    >
                      <XCircle className="w-4 h-4 mr-2" />
                      Cancel Subscription
                    </Button>
                  </div>
                </>
              )}

              {subscription.status === 'paused' && (
                <div>
                  <h3 className="font-semibold mb-2 flex items-center gap-2">
                    <PlayCircle className="w-4 h-4" />
                    Resume Subscription
                  </h3>
                  {subscription.paused_until && (
                    <p className="text-sm text-slate-600 mb-3">
                      Scheduled to resume on {new Date(subscription.paused_until).toLocaleDateString()}
                    </p>
                  )}
                  <Button
                    onClick={handleResume}
                    className="w-full bg-emerald-500 hover:bg-emerald-600"
                  >
                    <PlayCircle className="w-4 h-4 mr-2" />
                    Resume Now
                  </Button>
                </div>
              )}

              {subscription.status === 'cancelled' && (
                <Alert className="border-slate-200 bg-slate-50">
                  <AlertDescription className="text-slate-700">
                    This subscription has been cancelled. To start a new subscription, book a cleaner and select the subscription option.
                  </AlertDescription>
                </Alert>
              )}

              <div className="pt-4 border-t">
                <h3 className="font-semibold mb-2">Need Help?</h3>
                <p className="text-sm text-slate-600 mb-3">
                  Have questions about your subscription?
                </p>
                <Button
                  onClick={() => navigate(createPageUrl('Support'))}
                  variant="outline"
                  className="w-full"
                >
                  Contact Support
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { useNavigate } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import {
  Sparkles, Star, Loader2, ArrowLeft, Award
} from 'lucide-react';
import { motion } from 'framer-motion';

export default function MatchedCleaners() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [cleaners, setCleaners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [bookingData, setBookingData] = useState(null);
  const [addOns, setAddOns] = useState({});

  useEffect(() => {
    loadMatchedCleaners();
  }, []);

  const loadMatchedCleaners = async () => {
    try {
      // Check auth
      const currentUser = await base44.auth.me();
      if (!currentUser || currentUser.user_type !== 'client') {
        navigate(createPageUrl('ClientSignup'));
        return;
      }
      setUser(currentUser);

      // Load booking data from localStorage
      const savedBooking = localStorage.getItem('bookingDraft');
      const savedAddOns = localStorage.getItem('bookingAddOns');

      if (!savedBooking) {
        navigate(createPageUrl('Home'));
        return;
      }

      const booking = JSON.parse(savedBooking);
      setBookingData(booking);

      if (savedAddOns) {
        setAddOns(JSON.parse(savedAddOns));
      }

      // Fetch all cleaners
      const allCleaners = await base44.entities.CleanerProfile.filter({ is_active: true });

      // Filter by location (within reasonable distance)
      // Sort by tier (Elite > Pro > Semi Pro > Developing)
      const tierOrder = { 'Elite': 4, 'Pro': 3, 'Semi Pro': 2, 'Developing': 1 };

      const sortedCleaners = allCleaners
        .sort((a, b) => {
          // First by tier
          const tierDiff = (tierOrder[b.tier] || 0) - (tierOrder[a.tier] || 0);
          if (tierDiff !== 0) return tierDiff;

          // Then by reliability score
          return (b.reliability_score || 0) - (a.reliability_score || 0);
        });

      setCleaners(sortedCleaners);
    } catch (error) {
      handleError(error, { userMessage: 'Error loading cleaners:', showToast: false });
      alert('Failed to load cleaners. Please try again.');
      navigate(createPageUrl('Home'));
    }
    setLoading(false);
  };

  const handleSelectCleaner = (cleaner) => {
    // Save selected cleaner
    localStorage.setItem('selectedCleaner', cleaner.user_email);

    // Navigate to add-ons selection first
    navigate(createPageUrl(`AdditionalServices?cleaner=${cleaner.user_email}`));
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-soft-cloud flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="w-16 h-16 animate-spin text-puretask-blue mx-auto mb-4" />
          <p className="text-gray-600 font-verdana">Finding perfect cleaners for you...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-soft-cloud p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <Button
            onClick={() => navigate(-1)}
            variant="ghost"
            className="mb-4 rounded-full font-fredoka"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back
          </Button>

          <div className="flex items-center gap-3 mb-4">
            <div className="w-14 h-14 brand-gradient rounded-2xl flex items-center justify-center shadow-lg">
              <Sparkles className="w-7 h-7 text-white" />
            </div>
            <div>
              <h1 className="text-4xl font-fredoka font-bold text-graphite">
                Available Cleaners for You
              </h1>
              <p className="text-gray-600 font-verdana">
                Showing {cleaners.length} verified cleaner{cleaners.length !== 1 ? 's' : ''} ‚Ä¢ Sorted by tier & rating
              </p>
            </div>
          </div>

          {/* Booking Summary */}
          {bookingData && (
            <Card className="border-0 shadow-lg rounded-2xl bg-gradient-to-r from-blue-50 to-cyan-50">
              <CardContent className="p-6">
                <div className="grid md:grid-cols-4 gap-4 text-center">
                  <div>
                    <p className="text-sm text-gray-600 font-verdana mb-1">Service</p>
                    <p className="font-fredoka font-bold text-graphite capitalize">{bookingData.serviceType}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-600 font-verdana mb-1">Date & Time</p>
                    <p className="font-fredoka font-bold text-graphite">
                      {new Date(bookingData.date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} ‚Ä¢ {bookingData.time}
                    </p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-600 font-verdana mb-1">Duration</p>
                    <p className="font-fredoka font-bold text-graphite">{bookingData.recommendedHours} hours</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-600 font-verdana mb-1">Add-Ons</p>
                    <p className="font-fredoka font-bold text-graphite">{Object.keys(addOns).length} selected</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Info Alert */}
        <Alert className="mb-8 border-2 border-purple-200 bg-purple-50 rounded-2xl">
          <Award className="w-5 h-5 text-purple-600" />
          <AlertDescription className="font-verdana text-gray-700">
            <strong>Tier Priority:</strong> Higher-tier cleaners appear first because they have proven track records of excellence. All cleaners are verified and background-checked.
          </AlertDescription>
        </Alert>

        {/* Cleaners List */}
        <div className="space-y-6">
          {cleaners.length > 0 ? (
            cleaners.map((cleaner, idx) => (
              <motion.div
                key={cleaner.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: idx * 0.1 }}
              >
                <Card className="border-0 shadow-xl rounded-2xl hover:shadow-2xl transition-all overflow-hidden">
                  <CardContent className="p-0">
                    <div className="grid lg:grid-cols-4 gap-6 p-6">
                      {/* Profile Section */}
                      <div className="lg:col-span-1">
                        <div className="relative">
                          {cleaner.profile_photo_url ? (
                            <img
                              src={cleaner.profile_photo_url}
                              alt={cleaner.full_name}
                              className="w-full h-48 object-cover rounded-xl"
                            />
                          ) : (
                            <div className="w-full h-48 brand-gradient rounded-xl flex items-center justify-center">
                              <span className="text-5xl text-white font-fredoka font-bold">
                                {cleaner.full_name?.[0] || 'C'}
                              </span>
                            </div>
                          )}
                          <Badge 
                            className="absolute top-3 right-3 font-fredoka font-bold shadow-lg"
                            style={{
                              backgroundColor: cleaner.tier === 'Elite' ? '#FBBF24' :
                                             cleaner.tier === 'Pro' ? '#66B3FF' :
                                             cleaner.tier === 'Semi Pro' ? '#00D4FF' : '#9CA3AF',
                              color: 'white'
                            }}
                          >
                            {cleaner.tier}
                          </Badge>
                        </div>
                        <h3 className="font-fredoka font-bold text-xl text-graphite mt-3">
                          {cleaner.full_name}
                        </h3>
                        <div className="flex items-center gap-2 mt-2">
                          <Star className="w-4 h-4 text-amber-500 fill-amber-500" />
                          <span className="font-fredoka font-semibold text-graphite">
                            {cleaner.average_rating?.toFixed(1) || '5.0'}
                          </span>
                          <span className="text-sm text-gray-500 font-verdana">
                            ({cleaner.total_reviews || 0} reviews)
                          </span>
                        </div>
                      </div>

                      {/* Details Section */}
                      <div className="lg:col-span-2">
                        {cleaner.bio && (
                          <p className="text-gray-600 font-verdana mb-4 line-clamp-3">
                            {cleaner.bio}
                          </p>
                        )}

                        <div className="grid grid-cols-2 gap-4 mb-4">
                          <div>
                            <p className="text-xs text-gray-500 font-verdana mb-1">Base Rate</p>
                            <p className="font-fredoka font-bold text-graphite">
                              {cleaner.base_rate_credits_per_hour || 300} credits/hr
                            </p>
                            <p className="text-xs text-gray-500 font-verdana">
                              ‚âà ${((cleaner.base_rate_credits_per_hour || 300) / 10).toFixed(0)}/hr
                            </p>
                          </div>
                          <div>
                            <p className="text-xs text-gray-500 font-verdana mb-1">Reliability</p>
                            <div className="flex items-center gap-2">
                              <div className="flex-1 bg-gray-200 rounded-full h-2">
                                <div 
                                  className="h-2 rounded-full"
                                  style={{
                                    width: `${cleaner.reliability_score || 75}%`,
                                    backgroundColor: cleaner.reliability_score >= 90 ? '#28C76F' :
                                                   cleaner.reliability_score >= 75 ? '#66B3FF' : '#9CA3AF'
                                  }}
                                />
                              </div>
                              <span className="font-fredoka font-bold text-sm">
                                {cleaner.reliability_score || 75}%
                              </span>
                            </div>
                          </div>
                        </div>

                        {cleaner.specialty_tags && cleaner.specialty_tags.length > 0 && (
                          <div className="flex flex-wrap gap-2">
                            {cleaner.specialty_tags.slice(0, 5).map((tag, i) => (
                              <Badge key={i} variant="outline" className="rounded-full font-fredoka text-xs">
                                {tag}
                              </Badge>
                            ))}
                          </div>
                        )}
                      </div>

                      {/* Action Section */}
                      <div className="lg:col-span-1 flex flex-col justify-center">
                        <div className="space-y-3">
                          <div className="p-4 bg-gradient-to-r from-purple-50 to-blue-50 rounded-xl border-2 border-purple-200 text-center">
                            <p className="text-sm text-gray-600 font-verdana mb-1">Estimated Total</p>
                            <p className="text-3xl font-fredoka font-bold text-purple-600">
                              {Math.round((cleaner.base_rate_credits_per_hour || 300) * (bookingData?.recommendedHours || 3))}
                            </p>
                            <p className="text-xs text-gray-500 font-verdana">credits</p>
                          </div>

                          <Button
                            onClick={() => handleSelectCleaner(cleaner)}
                            className="w-full brand-gradient text-white rounded-full font-fredoka font-bold py-6 shadow-xl hover:shadow-2xl"
                          >
                            Select Cleaner
                          </Button>

                          <Button
                            onClick={() => navigate(createPageUrl(`CleanerProfile?email=${cleaner.user_email}`))}
                            variant="outline"
                            className="w-full rounded-full font-fredoka font-semibold border-2"
                          >
                            View Full Profile
                          </Button>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            ))
          ) : (
            <Card className="border-0 shadow-xl rounded-2xl">
              <CardContent className="p-12 text-center">
                <Sparkles className="w-20 h-20 text-gray-300 mx-auto mb-4" />
                <h3 className="font-fredoka font-bold text-graphite text-2xl mb-2">
                  No cleaners available
                </h3>
                <p className="text-gray-600 font-verdana mb-6">
                  Try adjusting your date/time or location
                </p>
                <Button
                  onClick={() => navigate(createPageUrl('Home'))}
                  className="brand-gradient text-white rounded-full font-fredoka font-semibold"
                >
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Go Back & Adjust
                </Button>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { useNavigate } from 'react-router-dom';
import { base44 } from '@/api/base44Client';
import { createPageUrl } from '@/utils';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { ArrowLeft, Plus, Repeat, Loader2, ArrowRight } from 'lucide-react';
import AddressAutocomplete from '@/components/address/AddressAutocomplete';
import MultiBookingCalendar from '@/components/booking/MultiBookingCalendar';
import BookingCart from '@/components/booking/BookingCart';
import RecurringBookingSetup from '@/components/booking/RecurringBookingSetup';

export default function MultiBooking() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [mode, setMode] = useState('individual'); // 'individual' or 'recurring'
  const [bookingCart, setBookingCart] = useState([]);
  const [currentBooking, setCurrentBooking] = useState({
    address: '',
    latitude: null,
    longitude: null,
    serviceType: 'basic',
    hours: 3,
    date: '',
    time: '',
    estimatedCredits: 0
  });
  const [selectedDates, setSelectedDates] = useState([]);
  const [appliedOffer, setAppliedOffer] = useState(null);
  const [showRecurringSetup, setShowRecurringSetup] = useState(false);

  useEffect(() => {
    checkUser();
  }, []);

  const checkUser = async () => {
    try {
      const currentUser = await base44.auth.me();
      if (!currentUser) {
        navigate(createPageUrl('SignIn'));
        return;
      }
      setUser(currentUser);
    } catch (error) {
      handleError(error, { userMessage: 'Error loading user:', showToast: false });
      navigate(createPageUrl('SignIn'));
    } finally {
      setLoading(false);
    }
  };

  const calculateEstimatedCredits = (booking) => {
    const baseRateMap = {
      basic: 300,
      deep: 330,
      moveout: 330
    };
    return baseRateMap[booking.serviceType] * booking.hours;
  };

  const addBookingToCart = () => {
    if (!currentBooking.date || !currentBooking.time) {
      alert('Please select a date and time');
      return;
    }

    const bookingWithEstimate = {
      ...currentBooking,
      estimatedCredits: calculateEstimatedCredits(currentBooking)
    };

    setBookingCart([...bookingCart, bookingWithEstimate]);

    // Reset current booking but keep address
    setCurrentBooking({
      ...currentBooking,
      date: '',
      time: '',
      estimatedCredits: 0
    });
  };

  const addMultipleDatesAsBookings = () => {
    if (selectedDates.length === 0) {
      alert('Please select at least one date');
      return;
    }
    if (!currentBooking.time) {
      alert('Please select a time');
      return;
    }

    const newBookings = selectedDates.map(date => ({
      ...currentBooking,
      date,
      estimatedCredits: calculateEstimatedCredits(currentBooking)
    }));

    setBookingCart([...bookingCart, ...newBookings]);
    setSelectedDates([]);
  };

  const removeBookingFromCart = (index) => {
    setBookingCart(bookingCart.filter((_, i) => i !== index));
  };

  const editBooking = (index) => {
    const booking = bookingCart[index];
    setCurrentBooking(booking);
    removeBookingFromCart(index);
  };

  const handleRecurringGenerate = (recurringData) => {
    const newBookings = recurringData.dates.map(date => ({
      ...currentBooking,
      date,
      estimatedCredits: calculateEstimatedCredits(currentBooking)
    }));

    setBookingCart([...bookingCart, ...newBookings]);
    setShowRecurringSetup(false);
    setMode('individual');
  };

  // Calculate totals and check for bundle offers
  const totalOriginalPrice = bookingCart.reduce((sum, b) => sum + b.estimatedCredits, 0);
  const totalDiscount = appliedOffer ? (totalOriginalPrice * (appliedOffer.discount_percentage / 100)) : 0;
  const finalTotalPrice = totalOriginalPrice - totalDiscount;

  useEffect(() => {
    checkBundleOffers();
  }, [bookingCart]);

  const checkBundleOffers = async () => {
    if (bookingCart.length === 0) {
      setAppliedOffer(null);
      return;
    }

    try {
      const offers = await base44.entities.BundleOffer.filter({
        offer_type: 'multi_booking',
        is_active: true
      });

      const qualifyingOffer = offers.find(offer => {
        const minBookings = offer.conditions?.min_bookings || 0;
        return bookingCart.length >= minBookings;
      });

      setAppliedOffer(qualifyingOffer || null);
    } catch (error) {
      handleError(error, { userMessage: 'Error checking bundle offers:', showToast: false });
    }
  };

  const calculateTotals = () => {
    const subtotal = totalOriginalPrice / 10; // Convert credits to dollars
    const discount = totalDiscount / 10;
    const finalTotal = finalTotalPrice / 10;
    return { subtotal, discount, finalTotal, appliedOffer };
  };

  const proceedToCheckout = () => {
    const totals = calculateTotals();
    const cartData = {
      bookings: bookingCart,
      subtotal: totals.subtotal,
      discount: totals.discount,
      finalTotal: totals.finalTotal,
      appliedOffer: totals.appliedOffer
    };
    localStorage.setItem('multiBookingCart', JSON.stringify(cartData));
    navigate(createPageUrl('MultiBookingCheckout'));
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-soft-cloud flex items-center justify-center">
        <Loader2 className="w-12 h-12 animate-spin text-puretask-blue" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-soft-cloud py-8">
      <div className="container mx-auto px-4 max-w-7xl">
        {/* Header */}
        <div className="mb-8">
          <Button
            variant="ghost"
            onClick={() => navigate(createPageUrl('ClientDashboard'))}
            className="mb-4 font-fredoka"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Dashboard
          </Button>
          <h1 className="text-4xl font-fredoka font-bold text-graphite mb-2">
            Multi-Booking
          </h1>
          <p className="text-gray-600 font-verdana">
            Book multiple cleanings and save with bundle discounts
          </p>
        </div>

        {/* Mode Selection */}
        <div className="grid grid-cols-2 gap-4 mb-8">
          <button
            onClick={() => {
              setMode('individual');
              setShowRecurringSetup(false);
            }}
            className={`p-4 rounded-2xl border-2 transition-all ${
              mode === 'individual'
                ? 'border-puretask-blue bg-blue-50 shadow-lg'
                : 'border-gray-300 bg-white hover:border-puretask-blue'
            }`}
          >
            <Plus className="w-8 h-8 mx-auto mb-2 text-puretask-blue" />
            <h3 className="font-fredoka font-bold text-graphite mb-1">Individual Bookings</h3>
            <p className="text-sm text-gray-600 font-verdana">Add multiple bookings one by one</p>
          </button>
          <button
            onClick={() => setMode('recurring')}
            className={`p-4 rounded-2xl border-2 transition-all ${
              mode === 'recurring'
                ? 'border-purple-500 bg-purple-50 shadow-lg'
                : 'border-gray-300 bg-white hover:border-purple-500'
            }`}
          >
            <Repeat className="w-8 h-8 mx-auto mb-2 text-purple-600" />
            <h3 className="font-fredoka font-bold text-graphite mb-1">Recurring Bookings</h3>
            <p className="text-sm text-gray-600 font-verdana">Set up repeating appointments</p>
          </button>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Left Column - Booking Form */}
          <div className="lg:col-span-2 space-y-6">
            {/* Common Fields */}
            <Card className="border-2 border-gray-200 rounded-2xl">
              <CardHeader>
                <CardTitle className="font-fredoka text-graphite">Booking Details</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {/* Address */}
                <AddressAutocomplete
                  value={currentBooking.address}
                  onChange={(address) => setCurrentBooking({ ...currentBooking, address })}
                  onLocationSelect={(location) => setCurrentBooking({
                    ...currentBooking,
                    address: location.address,
                    latitude: location.latitude,
                    longitude: location.longitude
                  })}
                  label="Service Address"
                  required
                />

                {/* Service Type */}
                <div>
                  <label className="block text-sm font-fredoka font-semibold mb-2 text-graphite">
                    Service Type
                  </label>
                  <div className="grid grid-cols-3 gap-3">
                    {[
                      { value: 'basic', label: 'Basic' },
                      { value: 'deep', label: 'Deep' },
                      { value: 'moveout', label: 'Move-Out' }
                    ].map(type => (
                      <button
                        key={type.value}
                        type="button"
                        onClick={() => setCurrentBooking({ ...currentBooking, serviceType: type.value })}
                        className={`p-3 rounded-xl font-fredoka font-semibold transition-all border-2 ${
                          currentBooking.serviceType === type.value
                            ? 'border-puretask-blue bg-puretask-blue text-white'
                            : 'border-gray-300 bg-white hover:border-puretask-blue'
                        }`}
                      >
                        {type.label}
                      </button>
                    ))}
                  </div>
                </div>

                {/* Hours */}
                <div>
                  <label className="block text-sm font-fredoka font-semibold mb-2 text-graphite">
                    Hours: {currentBooking.hours}
                  </label>
                  <div className="grid grid-cols-5 gap-2">
                    {[2, 3, 4, 5, 6, 8].map(hours => (
                      <button
                        key={hours}
                        type="button"
                        onClick={() => setCurrentBooking({ ...currentBooking, hours })}
                        className={`p-3 rounded-xl font-fredoka font-bold transition-all border-2 ${
                          currentBooking.hours === hours
                            ? 'bg-green-500 text-white border-green-600'
                            : 'bg-white text-gray-700 border-gray-300 hover:border-green-400'
                        }`}
                      >
                        {hours}
                      </button>
                    ))}
                  </div>
                </div>

                {/* Time */}
                <div>
                  <label className="block text-sm font-fredoka font-semibold mb-2 text-graphite">
                    Start Time
                  </label>
                  <select
                    value={currentBooking.time}
                    onChange={(e) => setCurrentBooking({ ...currentBooking, time: e.target.value })}
                    className="w-full rounded-xl font-verdana border-2 h-12 px-4"
                    required
                  >
                    <option value="">Select time</option>
                    {['08:00', '09:00', '10:00', '11:00', '12:00', '13:00', '14:00', '15:00', '16:00'].map(time => (
                      <option key={time} value={time}>{time}</option>
                    ))}
                  </select>
                </div>
              </CardContent>
            </Card>

            {/* Individual Booking Mode */}
            {mode === 'individual' && !showRecurringSetup && (
              <>
                <MultiBookingCalendar
                  selectedDates={selectedDates}
                  onDatesChange={setSelectedDates}
                />

                <div className="flex gap-3">
                  <Button
                    onClick={addMultipleDatesAsBookings}
                    disabled={selectedDates.length === 0 || !currentBooking.time}
                    className="flex-1 bg-puretask-blue hover:bg-blue-600 text-white rounded-full font-fredoka font-semibold py-6"
                  >
                    <Plus className="w-5 h-5 mr-2" />
                    Add {selectedDates.length} Booking{selectedDates.length !== 1 ? 's' : ''} to Cart
                  </Button>
                  <Button
                    onClick={() => setShowRecurringSetup(true)}
                    variant="outline"
                    className="border-2 border-purple-500 text-purple-600 hover:bg-purple-50 rounded-full font-fredoka font-semibold py-6"
                  >
                    <Repeat className="w-5 h-5 mr-2" />
                    Set Up Recurring
                  </Button>
                </div>
              </>
            )}

            {/* Recurring Booking Mode */}
            {(mode === 'recurring' || showRecurringSetup) && currentBooking.date && (
              <RecurringBookingSetup
                bookingTemplate={currentBooking}
                onGenerateDates={handleRecurringGenerate}
                onCancel={() => {
                  setShowRecurringSetup(false);
                  setMode('individual');
                }}
              />
            )}
          </div>

          {/* Right Column - Cart */}
          <div className="lg:col-span-1">
            <BookingCart
              bookings={bookingCart}
              onRemoveBooking={removeBookingFromCart}
              onEditBooking={editBooking}
              appliedOffer={appliedOffer}
              totalOriginalPrice={totalOriginalPrice}
              totalDiscount={totalDiscount}
              finalTotalPrice={finalTotalPrice}
            />

            {bookingCart.length > 0 && (
              <Button
                onClick={proceedToCheckout}
                className="w-full mt-4 bg-gradient-to-r from-puretask-blue to-cyan-500 text-white rounded-full font-fredoka font-bold py-6 text-lg shadow-xl hover:shadow-2xl"
              >
                Proceed to Checkout
                <ArrowRight className="w-5 h-5 ml-2" />
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// Notification Test Page
// Interactive page to test all notification flows

import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Badge } from '@/components/ui/badge';
import { Loader2, Bell, MessageSquare, Smartphone, Zap, CheckCircle2, AlertTriangle } from 'lucide-react';
import { jobEventBus } from '@/services/jobEvents';
import { clientNotificationService } from '@/services/clientNotificationService';
import { smsService, ClientSMSTemplates } from '@/services/smsService';
import { pushNotificationService, ClientPushTemplates } from '@/services/pushNotificationService';
import { realTimeNotificationService } from '@/services/realTimeNotificationService';
import { notificationIntegration } from '@/services/notificationIntegration';

export default function NotificationTestPage() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [testJob, setTestJob] = useState(null);
  const [testCleaner, setTestCleaner] = useState(null);
  const [testClient, setTestClient] = useState(null);
  const [notifications, setNotifications] = useState([]);
  const [systemStatus, setSystemStatus] = useState({
    inApp: true,
    email: true,
    sms: false,
    push: false,
    realTime: 'polling'
  });

  useEffect(() => {
    initialize();
  }, []);

  const initialize = async () => {
    try {
      setLoading(true);
      
      // Try to get current user (but don't fail if not logged in)
      let currentUser = null;
      try {
        currentUser = await base44.auth.me();
        setUser(currentUser);
      } catch (authError) {
        console.log('Not logged in - using mock data for testing');
      }

      // Check system capabilities
      checkSystemStatus();

      // Initialize notification integration (may fail without auth, that's OK)
      try {
        await notificationIntegration.initialize();
        
        // Initialize real-time notifications if we have a user
        if (currentUser) {
          await notificationIntegration.initializeRealTime(currentUser.email);
        }
      } catch (integrationError) {
        console.log('Notification integration skipped:', integrationError.message);
      }

      // Subscribe to notifications
      clientNotificationService.subscribe((notification) => {
        addNotificationLog('CLIENT_SERVICE', notification);
      });

      realTimeNotificationService.subscribe((notification) => {
        addNotificationLog('REAL_TIME', notification);
      });

      // ALWAYS create test job data (works without auth)
      createTestData();

    } catch (error) {
      console.error('Failed to initialize:', error);
      // Even if initialization fails, create test data so buttons work
      createTestData();
    } finally {
      setLoading(false);
    }
  };

  const checkSystemStatus = () => {
    setSystemStatus({
      inApp: true,
      email: true,
      sms: smsService.isEnabled(),
      push: pushNotificationService.isEnabled(),
      realTime: realTimeNotificationService.getMode()
    });
  };

  const createTestData = () => {
    // Create mock job with CORRECT property names from schema
    const mockJob = {
      id: 'test_job_' + Date.now(),
      client_id: user?.email || 'client@test.com',
      client_email: user?.email || 'client@test.com',
      assigned_cleaner_id: 'cleaner@test.com',
      assigned_cleaner_email: 'cleaner@test.com',
      status: 'OFFERED', // legacy
      state: 'OFFERED',
      sub_state: 'NONE',
      date: new Date().toISOString().split('T')[0],
      time: '09:00', // ‚úÖ CORRECT: scheduled time
      duration_hours: 3, // ‚úÖ CORRECT: not estimated_hours
      pricing_snapshot: { // ‚úÖ CORRECT: price is in snapshot
        total_price: 120,
        hourly_rate: 40,
        base_price: 100,
        breakdown: { base: 100, total: 120 }
      },
      latitude: 34.0522,
      longitude: -118.2437,
      address: '123 Main St, Los Angeles, CA 90012',
      cleaning_type: 'deep',
      bedrooms: 2,
      bathrooms: 2,
      square_feet: 1200,
      // Timestamps (all null initially)
      created_at: new Date().toISOString(),
      assigned_at: null,
      start_at: null,
      end_at: null,
      // Flags
      has_pending_extra_time_request: false,
      has_pending_reschedule_request: false,
      requires_before_photos: true,
      requires_after_photos: true,
      before_photos_count: 0,
      after_photos_count: 0
    };

    const mockCleaner = {
      id: 'cleaner_profile_1',
      user_email: 'cleaner@test.com',
      name: 'Jane Smith',
      phone: '+12345678901',
      payout_percentage: 0.8,
      reliability_score: 95
