// AI Photo Quality Validation Service
// Automatically validates photo quality using AI vision analysis

import React from 'react';
import { base44 } from '@/api/base44Client';

export interface PhotoQualityResult {
  score: number; // 0-100
  passed: boolean;
  issues: string[];
  suggestions: string[];
  analysis: {
    brightness: 'good' | 'too_dark' | 'too_bright';
    blur: 'sharp' | 'slightly_blurry' | 'too_blurry';
    framing: 'good' | 'poor' | 'cropped';
    relevance: 'relevant' | 'unclear' | 'irrelevant';
  };
}

export class PhotoQualityService {
  
  // Validate a single photo
  async validatePhoto(
    photoFile: File,
    photoType: 'before' | 'after',
    jobContext?: {
      cleaningType: string;
      room?: string;
    }
  ): Promise<PhotoQualityResult> {
    try {
      // Convert file to base64
      const base64 = await this.fileToBase64(photoFile);
      
      // Build prompt for AI vision analysis
      const prompt = this.buildValidationPrompt(photoType, jobContext);
      
      // Call Base44 LLM with vision capabilities
      const response = await base44.integrations.Core.InvokeLLM({
        model: 'gpt-4-vision-preview',
        messages: [
          {
            role: 'system',
            content: 'You are a professional cleaning photo quality validator. Analyze photos and provide structured feedback.'
          },
          {
            role: 'user',
            content: [
              { type: 'text', text: prompt },
              { type: 'image_url', image_url: { url: base64 } }
            ]
          }
        ],
        temperature: 0.3,
        max_tokens: 500
      });
      
      // Parse AI response
      const analysis = this.parseAIResponse(response.choices[0].message.content);
      
      return analysis;
    } catch (error) {
      console.error('Photo validation error:', error);
      // Fallback to basic validation
      return this.basicValidation(photoFile);
    }
  }
  
  // Build validation prompt based on photo type
  private buildValidationPrompt(
    photoType: 'before' | 'after',
    context?: { cleaningType: string; room?: string }
  ): string {
    const basePrompt = `Analyze this ${photoType} cleaning photo and evaluate:
    
1. **Brightness**: Is the photo well-lit and clear?
2. **Blur**: Is the photo sharp and in focus?
3. **Framing**: Does it show the relevant area clearly?
4. **Relevance**: Does it show cleaning progress/results?

${photoType === 'before' ? `
This is a BEFORE photo. Look for:
- Clear view of the area before cleaning
- Visible dirt, mess, or items to be cleaned
- Good angle showing the overall space
` : `
This is an AFTER photo. Look for:
- Clear view of the cleaned area
- Visible cleaning results
- Good comparison potential with before photos
`}

${context ? `Context: ${context.cleaningType} cleaning${context.room ? ` in ${context.room}` : ''}` : ''}

Respond in JSON format:
{
  "score": 0-100,
  "passed": true/false,
  "issues": ["issue 1", "issue 2"],
  "suggestions": ["suggestion 1", "suggestion 2"],
  "brightness": "good|too_dark|too_bright",
  "blur": "sharp|slightly_blurry|too_blurry",
  "framing": "good|poor|cropped",
  "relevance": "relevant|unclear|irrelevant"
}`;

    return basePrompt;
  }
  
  // Parse AI response into structured result
  private parseAIResponse(aiResponse: string): PhotoQualityResult {
    try {
      // Try to extract JSON from response
      const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
      if (!jsonMatch) throw new Error('No JSON found');
      
      const parsed = JSON.parse(jsonMatch[0]);
      
      return {
        score: parsed.score || 0,
        passed: parsed.passed || parsed.score >= 70,
        issues: parsed.issues || [],
        suggestions: parsed.suggestions || [],
        analysis: {
          brightness: parsed.brightness || 'good',
          blur: parsed.blur || 'sharp',
          framing: parsed.framing || 'good',
          relevance: parsed.relevance || 'relevant'
        }
      };
    } catch (error) {
      console.error('Failed to parse AI response:', error);
      return this.createDefaultResult();
    }
  }
  
  // Basic validation without AI (fallback)
  private async basicValidation(photoFile: File): Promise<PhotoQualityResult> {
    const sizeKB = photoFile.size / 1024;
    const issues: string[] = [];
    const suggestions: string[] = [];
    let score = 100;
    
    // Check file size
    if (sizeKB < 100) {
      issues.push('Photo resolution may be too low');
      suggestions.push('Take photo in higher quality mode');
      score -= 30;
    }
    
    if (sizeKB > 10000) {
      issues.push('Photo file size is very large');
      suggestions.push('Photo will take longer to upload');
      score -= 10;
    }
    
    // Check dimensions
    try {
      const dimensions = await this.getImageDimensions(photoFile);
      if (dimensions.width < 640 || dimensions.height < 480) {
        issues.push('Image dimensions are small');
        suggestions.push('Use a camera with higher resolution');
        score -= 20;
      }
    } catch (error) {
      // Couldn't get dimensions
    }
    
    return {
      score,
      passed: score >= 70,
      issues,
      suggestions,
      analysis: {
        brightness: 'good',
        blur: 'sharp',
        framing: 'good',
        relevance: 'relevant'
      }
    };
  }
  
  // Batch validate multiple photos
  async validateBatch(
    photos: File[],
    photoType: 'before' | 'after',
    jobContext?: { cleaningType: string }
  ): Promise<PhotoQualityResult[]> {
    const results = await Promise.all(
      photos.map(photo => this.validatePhoto(photo, photoType, jobContext))
    );
    return results;
  }
  
  // Get overall quality score for a set of photos
  getOverallScore(results: PhotoQualityResult[]): {
    avgScore: number;
    allPassed: boolean;
    totalIssues: number;
  } {
    const avgScore = results.reduce((sum, r) => sum + r.score, 0) / results.length;
    const allPassed = results.every(r => r.passed);
    const totalIssues = results.reduce((sum, r) => sum + r.issues.length, 0);
    
    return { avgScore, allPassed, totalIssues };
  }
  
  // Generate improvement tips based on common issues
  generateImprovementTips(results: PhotoQualityResult[]): string[] {
    const allIssues = results.flatMap(r => r.issues);
    const tips: string[] = [];
    
    if (allIssues.some(i => i.includes('dark'))) {
      tips.push('üí° Turn on more lights or open curtains for better brightness');
    }
    
    if (allIssues.some(i => i.includes('blur'))) {
      tips.push('üì∏ Hold your phone steady and tap to focus before taking the photo');
    }
    
    if (allIssues.some(i => i.includes('framing') || i.includes('cropped'))) {
      tips.push('üéØ Step back a bit to show more of the area in frame');
    }
    
    if (allIssues.some(i => i.includes('relevance') || i.includes('unclear'))) {
      tips.push('‚ú® Focus on areas that show cleaning progress or results');
    }
    
    return tips;
  }
  
  // Helper: Convert file to base64
  private fileToBase64(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = reject;
      reader.readAsDataURL(file);
    });
  }
  
  // Helper: Get image dimensions
  private getImageDimensions(file: File): Promise<{ width: number; height: number }> {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => resolve({ width: img.width, height: img.height });
      img.onerror = reject;
      img.src = URL.createObjectURL(file);
    });
  }
  
  // Helper: Create default result
  private createDefaultResult(): PhotoQualityResult {
    return {
      score: 70,
      passed: true,
      issues: [],
      suggestions: [],
      analysis: {
        brightness: 'good',
        blur: 'sharp',
        framing: 'good',
        relevance: 'relevant'
      }
    };
  }
}

// Singleton instance
export const photoQualityService = new PhotoQualityService();

// React Hook for photo validation
export function usePhotoValidation() {
  const [validating, setValidating] = React.useState(false);
  const [results, setResults] = React.useState<PhotoQualityResult[]>([]);
  
  const validatePhoto = async (
    file: File,
    type: 'before' | 'after',
    context?: { cleaningType: string }
  ) => {
    setValidating(true);
    try {
      const result = await photoQualityService.validatePhoto(file, type, context);
      setResults(prev => [...prev, result]);
      return result;
    } finally {
      setValidating(false);
    }
  };
  
  const clearResults = () => setResults([]);
  
  return {
    validatePhoto,
    validating,
    results,
    clearResults,
    overallScore: photoQualityService.getOverallScore(results),
    improvementTips: photoQualityService.generateImprovementTips(results)
  };
}

// Proactive Notification Service
// Smart notifications based on job state, time, and context

import React from 'react';
import type { JobRecord } from '@/types/cleanerJobTypes';
import { jobEventBus } from './jobEvents';

export interface Notification {
  id: string;
  type: 'reminder' | 'alert' | 'tip' | 'achievement' | 'warning';
  priority: 'high' | 'medium' | 'low';
  title: string;
  message: string;
  actionLabel?: string;
  actionUrl?: string;
  timestamp: Date;
  read: boolean;
  jobId?: string;
}

export class ProactiveNotificationService {
  private notifications: Notification[] = [];
  private listeners: Array<(notification: Notification) => void> = [];
  
  constructor() {
    this.setupEventListeners();
    this.startScheduledChecks();
  }
  
  // Subscribe to new notifications
  subscribe(callback: (notification: Notification) => void) {
    this.listeners.push(callback);
    return () => {
      this.listeners = this.listeners.filter(cb => cb !== callback);
    };
  }
  
  // Emit notification to all listeners
  private emit(notification: Notification) {
    this.notifications.push(notification);
    this.listeners.forEach(callback => callback(notification));
  }
  
  // Setup job event listeners
  private setupEventListeners() {
    // Job assigned - remind to prepare
    jobEventBus.on('JOB_ASSIGNED', async (event) => {
      this.emit({
        id: Date.now().toString(),
        type: 'reminder',
        priority: 'medium',
        title: 'üéØ New Job Assigned!',
        message: 'You have a new job! Review the details and mark when you\'re en route.',
        actionLabel: 'View Job',
        actionUrl: `/CleanerJobDetail/${event.jobId}`,
        timestamp: new Date(),
        read: false,
        jobId: event.jobId
      });
    });
    
    // Extra time approved
    jobEventBus.on('EXTRA_TIME_APPROVED', async (event) => {
      this.emit({
        id: Date.now().toString(),
        type: 'alert',
        priority: 'high',
        title: '‚úÖ Extra Time Approved!',
        message: `Client approved ${event.minutesApproved} extra minutes. Continue working!`,
        timestamp: new Date(),
        read: false,
        jobId: event.jobId
      });
    });
    
    // Job completed - celebrate
    jobEventBus.on('CLIENT_APPROVED', async (event) => {
      this.emit({
        id: Date.now().toString(),
        type: 'achievement',
        priority: 'medium',
        title: 'üéâ Job Approved!',
        message: `Great work! Your payment is being processed. Rating: ${event.rating}/5.0`,
        timestamp: new Date(),
        read: false,
        jobId: event.jobId
      });
    });
  }
  
  // Start scheduled checks for time-based notifications
  private startScheduledChecks() {
    // Check every minute for upcoming jobs
    setInterval(() => {
      this.checkUpcomingJobs();
      this.checkLateJobs();
      this.checkPhotoReminders();
    }, 60000); // Every 1 minute
  }
  
  // Check for jobs starting soon
  async checkUpcomingJobs() {
    // This would fetch from backend
    // For now, placeholder
  }
  
  // Generate notification for job starting soon
  createJobStartingSoonNotification(job: JobRecord, minutesUntil: number): Notification {
    let priority: 'high' | 'medium' | 'low' = 'medium';
    let title = '';
    let message = '';
    
    if (minutesUntil <= 15) {
      priority = 'high';
      title = 'üö® Job Starting Soon!';
      message = `Your job at ${job.address} starts in ${minutesUntil} minutes! Time to head out.`;
    } else if (minutesUntil <= 30) {
      priority = 'medium';
      title = '‚è∞ Job in 30 Minutes';
      message = `Don't forget: Job at ${job.address} starts soon. Prepare your supplies!`;
    } else if (minutesUntil <= 60) {
      priority = 'low';
      title = 'üìÖ Job in 1 Hour';
      message = `Heads up: You have a job at ${job.address} in about an hour.`;
    }
    
    return {
      id: `job-reminder-${job.id}-${Date.now()}`,
      type: 'reminder',
      priority,
      title,
      message,
      actionLabel: 'View Job',
      actionUrl: `/CleanerJobDetail/${job.id}`,
      timestamp: new Date(),
      read: false,
      jobId: job.id
    };
  }
  
  // Check for jobs that are late
  checkLateJobs() {
    // Placeholder - would check jobs marked EN_ROUTE but not ARRIVED after expected time
  }
  
  // Create late arrival notification
  createLateArrivalNotification(job: JobRecord, minutesLate: number): Notification {
    return {
      id: `late-${job.id}-${Date.now()}`,
      type: 'warning',
      priority: 'high',
      title: '‚ö†Ô∏è Running Late',
      message: `You're ${minutesLate} minutes late for your job. Contact the client if needed.`,
      actionLabel: 'Contact Support',
      actionUrl: '/Support',
      timestamp: new Date(),
      read: false,
      jobId: job.id
    };
  }
  
  // Check for photo reminders
  checkPhotoReminders() {
    // Placeholder - would check jobs IN_PROGRESS for > 30 min without photos
  }
  
  // Create photo reminder
  createPhotoReminderNotification(job: JobRecord, photoType: 'before' | 'after'): Notification {
    return {
      id: `photo-${job.id}-${photoType}-${Date.now()}`,
      type: 'reminder',
      priority: 'medium',
      title: 'üì∏ Photo Reminder',
      message: photoType === 'before' 
        ? 'Don\'t forget to take before photos before you start cleaning!'
        : 'Remember to take after photos to complete the job.',
      actionLabel: 'Upload Photos',
      actionUrl: `/CleanerJobDetail/${job.id}`,
      timestamp: new Date(),
      read: false,
      jobId: job.id
    };
  }
  
  // Create achievement notifications
  createAchievementNotification(
    title: string,
    message: string,
    milestone: string
  ): Notification {
    return {
      id: `achievement-${Date.now()}`,
      type: 'achievement',
      priority: 'low',
      title: `üèÜ ${title}`,
      message,
      timestamp: new Date(),
      read: false
    };
  }
  
  // Smart tips based on context
  createContextualTip(job: JobRecord): Notification {
    const tips = [
      {
        condition: (j: JobRecord) => j.cleaning_type === 'deep',
        title: 'üí° Deep Cleaning Tip',
        message: 'For deep cleans, focus on baseboards, light fixtures, and inside appliances for 5-star reviews!'
      },
      {
        condition: (j: JobRecord) => j.bedrooms >= 3,
        title: 'üí° Large Home Tip',
        message: 'Start with the farthest room and work your way back to save time and energy.'
      },
      {
        condition: (j: JobRecord) => j.cleaning_type === 'moveout',
        title: 'üí° Move-Out Tip',
        message: 'Check inside cabinets, closets, and drawers - these are often missed!'
      }
    ];
    
    const matchingTip = tips.find(tip => tip.condition(job));
    
    if (matchingTip) {
      return {
        id: `tip-${job.id}-${Date.now()}`,
        type: 'tip',
        priority: 'low',
        title: matchingTip.title,
        message: matchingTip.message,
        timestamp: new Date(),
        read: false,
        jobId: job.id
      };
    }
    
    return null as any;
  }
  
  // Weekly summary notification
  createWeeklySummaryNotification(stats: {
    jobsCompleted: number;
    earnings: number;
    avgRating: number;
  }): Notification {
    return {
      id: `weekly-${Date.now()}`,
      type: 'achievement',
      priority: 'low',
      title: 'üìä Weekly Summary',
      message: `Great week! ${stats.jobsCompleted} jobs completed, $${stats.earnings.toFixed(2)} earned, ${stats.avgRating.toFixed(1)}‚≠ê avg rating.`,
      actionLabel: 'View Analytics',
      actionUrl: '/CleanerDashboard',
      timestamp: new Date(),
      read: false
    };
  }
  
  // Mark notification as read
  markAsRead(notificationId: string) {
    const notification = this.notifications.find(n => n.id === notificationId);
    if (notification) {
      notification.read = true;
    }
  }
  
  // Get all notifications
  getAll(): Notification[] {
    return this.notifications.sort((a, b) => 
      b.timestamp.getTime() - a.timestamp.getTime()
    );
  }
  
  // Get unread count
  getUnreadCount(): number {
    return this.notifications.filter(n => !n.read).length;
  }
  
  // Clear old notifications
  clearOld(daysOld: number = 7) {
    const cutoff = new Date();
    cutoff.setDate(cutoff.getDate() - daysOld);
    
    this.notifications = this.notifications.filter(
      n => n.timestamp >= cutoff || !n.read
    );
  }
}

// Singleton instance
export const notificationService = new ProactiveNotificationService();

// React Hook for notifications
export function useNotifications() {
  const [notifications, setNotifications] = React.useState<Notification[]>([]);
  const [unreadCount, setUnreadCount] = React.useState(0);
  
  React.useEffect(() => {
    // Load initial notifications
    setNotifications(notificationService.getAll());
    setUnreadCount(notificationService.getUnreadCount());
    
    // Subscribe to new notifications
    const unsubscribe = notificationService.subscribe((notification) => {
      setNotifications(prev => [notification, ...prev]);
      setUnreadCount(prev => prev + 1);
    });
    
    return unsubscribe;
  }, []);
  
  const markAsRead = (notificationId: string) => {
    notificationService.markAsRead(notificationId);
    setNotifications(notificationService.getAll());
    setUnreadCount(notificationService.getUnreadCount());
  };
  
  const clearOld = () => {
    notificationService.clearOld();
    setNotifications(notificationService.getAll());
  };
  
  return {
    notifications,
    unreadCount,
    markAsRead,
    clearOld
  };
}

// Push Notification Service
// Browser push notifications for instant alerts (even when app is closed)

import { base44 } from '@/api/base44Client';

/**
 * Push Notification Service
 * Uses Web Push API for browser notifications
 * 
 * Features:
 * - Works even when browser tab is closed
 * - Native OS notifications
 * - Click to open relevant page
 * - Rich notifications with icons and images
 * 
 * Setup Instructions:
 * 1. Generate VAPID keys: https://web-push-codelab.glitch.me/
 * 2. Add to .env.local:
 *    VITE_VAPID_PUBLIC_KEY=your_public_key
 * 3. Add service worker for background notifications
 */

interface PushNotificationPayload {
  title: string;
  body: string;
  icon?: string;
  badge?: string;
  image?: string;
  data?: any;
  actions?: Array<{ action: string; title: string }>;
  tag?: string;
  requireInteraction?: boolean;
}

interface PushSubscriptionData {
  endpoint: string;
  keys: {
    p256dh: string;
    auth: string;
  };
}

export class PushNotificationService {
  private enabled: boolean = false;
  private vapidPublicKey: string | undefined;
  private registration: ServiceWorkerRegistration | null = null;
  private subscription: PushSubscription | null = null;

  constructor() {
    this.vapidPublicKey = import.meta.env.VITE_VAPID_PUBLIC_KEY;
    this.initialize();
  }

  /**
   * Initialize push notification service
   */
  private async initialize() {
    // Check if service workers are supported
    if (!('serviceWorker' in navigator)) {
      console.warn('[PushNotificationService] Service workers not supported');
      return;
    }

    // Check if Push API is supported
    if (!('PushManager' in window)) {
      console.warn('[PushNotificationService] Push API not supported');
      return;
    }

    // Check if Notification API is supported
    if (!('Notification' in window)) {
      console.warn('[PushNotificationService] Notifications not supported');
      return;
    }

    try {
      // Register service worker
      this.registration = await navigator.serviceWorker.register('/sw.js');
      console.log('[PushNotificationService] Service worker registered');

      // Check existing subscription
      this.subscription = await this.registration.pushManager.getSubscription();
      
      if (this.subscription) {
        console.log('[PushNotificationService] Existing push subscription found');
        this.enabled = true;
      } else {
        console.log('[PushNotificationService] No existing push subscription');
      }
    } catch (error) {
      console.error('[PushNotificationService] Initialization error:', error);
    }
  }

  /**
   * Request permission and subscribe to push notifications
   */
  async requestPermission(): Promise<boolean> {
    if (!('Notification' in window)) {
      console.warn('[PushNotificationService] Notifications not supported');
      return false;
    }

    try {
      const permission = await Notification.requestPermission();
      
      if (permission === 'granted') {
        console.log('[PushNotificationService] Permission granted');
        
        // Subscribe to push
        const subscribed = await this.subscribe();
        
        if (subscribed) {
          this.enabled = true;
          return true;
        }
      } else if (permission === 'denied') {
        console.warn('[PushNotificationService] Permission denied');
      } else {
        console.log('[PushNotificationService] Permission dismissed');
      }
      
      return false;
    } catch (error) {
      console.error('[PushNotificationService] Permission request error:', error);
      return false;
    }
  }

  /**
   * Subscribe to push notifications
   */
  private async subscribe(): Promise<boolean> {
    if (!this.registration) {
      console.error('[PushNotificationService] No service worker registration');
      return false;
    }

    if (!this.vapidPublicKey) {
      console.error('[PushNotificationService] VAPID public key not configured');
      return false;
    }

    try {
      // Convert VAPID key from base64 to Uint8Array
      const vapidKey = this.urlBase64ToUint8Array(this.vapidPublicKey);
      
      // Subscribe
      this.subscription = await this.registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: vapidKey
      });

      console.log('[PushNotificationService] Subscribed to push notifications');

      // Save subscription to database
      await this.saveSubscription(this.subscription);

      return true;
    } catch (error) {
      console.error('[PushNotificationService] Subscribe error:', error);
      return false;
    }
  }

  /**
   * Unsubscribe from push notifications
   */
  async unsubscribe(): Promise<boolean> {
    if (!this.subscription) {
      return false;
    }

    try {
      await this.subscription.unsubscribe();
      console.log('[PushNotificationService] Unsubscribed from push notifications');

      // Remove subscription from database
      await this.removeSubscription(this.subscription);

      this.subscription = null;
      this.enabled = false;
      return true;
    } catch (error) {
      console.error('[PushNotificationService] Unsubscribe error:', error);
      return false;
    }
  }

  /**
   * Save push subscription to database
   */
  private async saveSubscription(subscription: PushSubscription) {
    try {
      const user = await base44.auth.me().catch(() => null);
      if (!user) return;

      const subscriptionData = subscription.toJSON();

      await base44.entities.PushSubscription?.create({
        user_email: user.email,
        endpoint: subscriptionData.endpoint,
        p256dh_key: subscriptionData.keys?.p256dh,
        auth_key: subscriptionData.keys?.auth,
        user_agent: navigator.userAgent,
        created_at: new Date().toISOString()
      }).catch(() => {
        // Entity doesn't exist yet
        console.log('[PushNotificationService] PushSubscription entity not found');
      });

      console.log('[PushNotificationService] Subscription saved to database');
    } catch (error) {
      console.error('[PushNotificationService] Failed to save subscription:', error);
    }
  }

  /**
   * Remove push subscription from database
   */
  private async removeSubscription(subscription: PushSubscription) {
    try {
      const user = await base44.auth.me().catch(() => null);
      if (!user) return;

      const subscriptionData = subscription.toJSON();

      // Find and delete subscription
      const subs = await base44.entities.PushSubscription?.filter({
        user_email: user.email,
        endpoint: subscriptionData.endpoint
      }).catch(() => []);

      if (subs && subs.length > 0) {
        await base44.entities.PushSubscription.delete(subs[0].id);
        console.log('[PushNotificationService] Subscription removed from database');
      }
    } catch (error) {
      console.error('[PushNotificationService] Failed to remove subscription:', error);
    }
  }

  /**
   * Show local push notification (doesn't require server)
   * Useful for immediate feedback while server-side push is being processed
   */
  async showLocalNotification(payload: PushNotificationPayload): Promise<boolean> {
    if (!this.registration) {
      console.warn('[PushNotificationService] No service worker registration');
      return false;
    }

    if (Notification.permission !== 'granted') {
      console.warn('[PushNotificationService] Notification permission not granted');
      return false;
    }

    try {
      await this.registration.showNotification(payload.title, {
        body: payload.body,
        icon: payload.icon || '/icon-192.png',
        badge: payload.badge || '/badge-72.png',
        image: payload.image,
        data: payload.data,
        actions: payload.actions,
        tag: payload.tag,
        requireInteraction: payload.requireInteraction || false,
        vibrate: [200, 100, 200]
      });

      console.log('[PushNotificationService] Local notification shown');
      return true;
    } catch (error) {
      console.error('[PushNotificationService] Show notification error:', error);
      return false;
    }
  }

  /**
   * Send push notification via server
   * The server will use the saved subscription to send a push message
   */
  async sendPushNotification(userEmail: string, payload: PushNotificationPayload): Promise<boolean> {
    try {
      // Call backend function to send push
      await base44.integrations.Core.InvokeServerlessFunction({
        functionName: 'sendPushNotification',
        payload: {
          recipientEmail: userEmail,
          notification: payload
        }
      });

      console.log(`[PushNotificationService] Push notification sent to ${userEmail}`);
      return true;
    } catch (error) {
      console.error('[PushNotificationService] Send push error:', error);
      return false;
    }
  }

  /**
   * Check if push notifications are enabled
   */
  isEnabled(): boolean {
    return this.enabled && Notification.permission === 'granted';
  }

  /**
   * Get current permission status
   */
  getPermissionStatus(): NotificationPermission {
    return Notification.permission;
  }

  /**
   * Check if push is supported
   */
  isSupported(): boolean {
    return 'serviceWorker' in navigator && 'PushManager' in window && 'Notification' in window;
  }

  /**
   * Convert VAPID key from base64 to Uint8Array
   */
  private urlBase64ToUint8Array(base64String: string): Uint8Array {
    const padding = '='.repeat((4 - (base64String.length % 4)) % 4);
    const base64 = (base64String + padding).replace(/-/g, '+').replace(/_/g, '/');
    const rawData = window.atob(base64);
    const outputArray = new Uint8Array(rawData.length);
    for (let i = 0; i < rawData.length; ++i) {
      outputArray[i] = rawData.charCodeAt(i);
    }
    return outputArray;
  }
}

